--

INSERT INTO `tintuc` (`Id`, `TenTinTuc`, `NoiDung`, `HinhAnh`, `NgayTao`, `Xoa`) VALUES
(1, 'Hà Nội Mùa Nước Lũ', '<p>Bộ ảnh n&agrave;y nhằm lưu lại những điểm đến đặc trưng của thủ đ&ocirc;, c&aacute;c c&ocirc;ng tr&igrave;nh vốn đ&atilde; qu&aacute; th&acirc;n thuộc với những người gắn b&oacute; với mảnh đất n&agrave;y.</p>\r\n\r\n<table align=\"center\">\r\n	<tbody>\r\n		<tr>\r\n			<td>\r\n			<p>&nbsp;</p>\r\n\r\n			<p><img alt=\"17 diem tham quan khong nen bo qua khi toi Ha Noi hinh anh 1\" src=\"https://znews-photo-td.zadn.vn/w1024/Uploaded/tmuitg/2016_08_29/ANh2.jpg\" style=\"height:1050px; width:1400px\" /></p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n			<p><strong>Nh&agrave; h&aacute;t lớn&nbsp;<a href=\"https://news.zing.vn/ha-noi-tieu-diem.html\" title=\"Tin tức Hà Nội\">H&agrave; Nội</a>:</strong>&nbsp;C&ocirc;ng tr&igrave;nh được người Ph&aacute;p khởi c&ocirc;ng x&acirc;y dựng năm 1901 v&agrave; ho&agrave;n th&agrave;nh năm 1911. Đ&acirc;y l&agrave; một trong những địa điểm thăm quan nổi tiếng tại thủ đ&ocirc;. T&ocirc;i th&iacute;ch nhất l&agrave; ngồi ở hi&ecirc;n nh&agrave; h&aacute;t lớn v&agrave;o buổi đ&ecirc;m, nghe nhạc v&agrave; ăn kem Tr&agrave;ng Tiền, ngắm d&ograve;ng người qua lại.&nbsp;&nbsp;</p>\r\n\r\n			<p>&nbsp;</p>\r\n\r\n			<p>&nbsp;</p>\r\n			</td>\r\n		</tr>\r\n	</tbody>\r\n</table>\r\n\r\n<table align=\"center\">\r\n	<tbody>\r\n		<tr>\r\n			<td>\r\n			<p>&nbsp;</p>\r\n\r\n			<p><img alt=\"17 diem tham quan khong nen bo qua khi toi Ha Noi hinh anh 2\" src=\"https://znews-photo-td.zadn.vn/w1024/Uploaded/tmuitg/2016_08_29/anh3.jpg\" style=\"height:1050px; width:1400px\" title=\"17 điểm tham quan không nên bỏ qua khi tới Hà Nội hình ảnh 2\" /></p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n			<p><strong>Nh&agrave; thờ lớn H&agrave; Nội&nbsp;</strong>(t&ecirc;n ch&iacute;nh thức: Nh&agrave; thờ ch&iacute;nh t&ograve;a Th&aacute;nh Giuse): Nh&agrave; thờ được ho&agrave;n th&agrave;nh v&agrave;o năm 1886 - l&agrave; nh&agrave; thờ ch&iacute;nh t&ograve;a của Tổng gi&aacute;o phận H&agrave; Nội, nơi c&oacute; ngai t&ograve;a của Tổng gi&aacute;m mục. Đ&acirc;y cũng l&agrave; một nh&agrave; thờ l&acirc;u đời ở H&agrave; Nội, thường xuy&ecirc;n diễn ra c&aacute;c sinh hoạt t&iacute;n ngưỡng của c&aacute;c gi&aacute;o d&acirc;n thuộc tổng gi&aacute;o phận H&agrave; Nội.&nbsp;</p>\r\n\r\n			<p>&nbsp;</p>\r\n\r\n			<p>&nbsp;</p>\r\n			</td>\r\n		</tr>\r\n	</tbody>\r\n</table>\r\n\r\n<table align=\"center\">\r\n	<tbody>\r\n		<tr>\r\n			<td>\r\n			<p>&nbsp;</p>\r\n\r\n			<p><img alt=\"17 diem tham quan khong nen bo qua khi toi Ha Noi hinh anh 3\" src=\"https://znews-photo-td.zadn.vn/w1024/Uploaded/tmuitg/2016_08_29/ANh4.jpg\" style=\"height:1009px; width:1400px\" title=\"17 điểm tham quan không nên bỏ qua khi tới Hà Nội hình ảnh 3\" /></p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n			<p><strong>Lăng Chủ tịch Hồ Ch&iacute; Minh (quận Ba Đ&igrave;nh):</strong>&nbsp;Hơn 10 năm qua, t&ocirc;i đi xe m&aacute;y qua qua lăng biết bao lần, nhưng lần n&agrave;y t&ocirc;i mới lại đứng gần như thế n&agrave;y.&nbsp;</p>\r\n\r\n			<p>&nbsp;</p>\r\n\r\n			<p>&nbsp;</p>\r\n			</td>\r\n		</tr>\r\n	</tbody>\r\n</table>\r\n\r\n<table align=\"center\">\r\n	<tbody>\r\n		<tr>\r\n			<td>\r\n			<p>&nbsp;</p>\r\n\r\n			<p><img alt=\"17 diem tham quan khong nen bo qua khi toi Ha Noi hinh anh 4\" src=\"https://znews-photo-td.zadn.vn/w1024/Uploaded/tmuitg/2016_08_29/Anh_5.jpg\" style=\"height:1050px; width:1400px\" title=\"17 điểm tham quan không nên bỏ qua khi tới Hà Nội hình ảnh 4\" /></p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n			<p>&nbsp;</p>\r\n\r\n			<p><strong>Ga H&agrave; Nội&nbsp;</strong>được x&acirc;y dựng v&agrave; ho&agrave;n th&agrave;nh v&agrave;o năm 1900, c&oacute; quy m&ocirc; lớn nhất Đ&ocirc;ng Dương thời kỳ đ&oacute;. Rất nhiều khi t&ocirc;i chỉ muốn nhảy l&ecirc;n t&agrave;u, đến một bến n&agrave;o đ&oacute;, v&agrave; bắt đầu kh&aacute;m ph&aacute;, chắc tuyệt lắm.</p>\r\n			&nbsp;\r\n\r\n			<p>&nbsp;</p>\r\n\r\n			<p>&nbsp;</p>\r\n			</td>\r\n		</tr>\r\n	</tbody>\r\n</table>\r\n\r\n<table align=\"center\">\r\n	<tbody>\r\n		<tr>\r\n			<td>\r\n			<p>&nbsp;</p>\r\n\r\n			<p><img alt=\"17 diem tham quan khong nen bo qua khi toi Ha Noi hinh anh 5\" src=\"https://znews-photo-td.zadn.vn/w1024/Uploaded/tmuitg/2016_08_29/Anh_6.jpg\" style=\"height:1050px; width:1400px\" /></p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n			<p>&nbsp;</p>\r\n\r\n			<p><strong>Con đường gốm sứ</strong>&nbsp;được x&acirc;y dựng v&agrave; ho&agrave;n th&agrave;nh năm 2008 nh&acirc;n dịp kỷ niệm 1000 năm Thăng Long - H&agrave; Nội. Nơi đ&acirc;y được tổ chức Guinness thế giới c&ocirc;ng nhận đ&acirc;y l&agrave; bức tranh gốm d&agrave;i nhất thế giới.</p>\r\n			&nbsp;\r\n\r\n			<p>&nbsp;</p>\r\n\r\n			<p>&nbsp;</p>\r\n			</td>\r\n		</tr>\r\n	</tbody>\r\n</table>\r\n\r\n<table align=\"center\">\r\n	<tbody>\r\n		<tr>\r\n			<td>\r\n			<p>&nbsp;</p>\r\n\r\n			<p><img alt=\"17 diem tham quan khong nen bo qua khi toi Ha Noi hinh anh 6\" src=\"https://znews-photo-td.zadn.vn/w1024/Uploaded/tmuitg/2016_08_29/ANh_7.jpg\" style=\"height:1050px; width:1400px\" title=\"17 điểm tham quan không nên bỏ qua khi tới Hà Nội hình ảnh 6\" /></p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n			<p><strong>Phủ T&acirc;y Hồ</strong>&nbsp;nằm tr&ecirc;n b&aacute;n đảo lớn giữa hồ T&acirc;y. Phủ thờ Liễu Hạnh c&ocirc;ng ch&uacute;a, một nh&acirc;n vật trong truyền thuyết, v&agrave; l&agrave; một trong bốn vị th&aacute;nh bất tử trong t&iacute;n ngưỡng của người Việt.&nbsp;Ng&agrave;y rằm, hay đầu th&aacute;ng, người d&acirc;n đến phủ T&acirc;y Hồ lễ b&aacute;i x&igrave; xụp. Cảnh chen lấn, x&ocirc; đẩy, m&oacute;c t&uacute;i h&ograve; h&eacute;t diễn ra thường xuy&ecirc;n tại nơi thanh tịnh, khiến m&igrave;nh kh&ocirc;ng bao giờ c&oacute; &yacute; định đến v&agrave;o những ng&agrave;y n&agrave;y</p>\r\n			&nbsp;\r\n\r\n			<p>.</p>\r\n			&nbsp;\r\n\r\n			<p>&nbsp;</p>\r\n			</td>\r\n		</tr>\r\n	</tbody>\r\n</table>\r\n\r\n<table align=\"center\">\r\n	<tbody>\r\n		<tr>\r\n			<td>\r\n			<p>&nbsp;</p>\r\n\r\n			<p><img alt=\"17 diem tham quan khong nen bo qua khi toi Ha Noi hinh anh 7\" src=\"https://znews-photo-td.zadn.vn/w1024/Uploaded/tmuitg/2016_08_29/ANh_9.jpg\" style=\"height:1050px; width:1400px\" title=\"17 điểm tham quan không nên bỏ qua khi tới Hà Nội hình ảnh 7\" /></p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n			<p>&nbsp;</p>\r\n\r\n			<p><strong>Th&aacute;c nước h&agrave;ng Đậu</strong>&nbsp;l&agrave; c&ocirc;ng tr&igrave;nh x&acirc;y dựng năm 1894, nằm tại ng&atilde; s&aacute;u của c&aacute;c phố cổ H&agrave;ng Than, H&agrave;ng Lược, H&agrave;ng Giấy, H&agrave;ng Đậu, Qu&aacute;n Th&aacute;nh v&agrave; đường Phan Đ&igrave;nh Ph&ugrave;ng.</p>\r\n			&nbsp;\r\n\r\n			<p>&nbsp;</p>\r\n\r\n			<p>&nbsp;</p>\r\n			</td>\r\n		</tr>\r\n	</tbody>\r\n</table>\r\n\r\n<table align=\"center\">\r\n	<tbody>\r\n		<tr>\r\n			<td>\r\n			<p>&nbsp;</p>\r\n\r\n			<p><img alt=\"17 diem tham quan khong nen bo qua khi toi Ha Noi hinh anh 8\" src=\"https://znews-photo-td.zadn.vn/w1024/Uploaded/tmuitg/2016_08_29/ANh_10.jpg\" style=\"height:1050px; width:1400px\" /></p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n			<p>&nbsp;</p>\r\n\r\n			<p><strong>Nh&agrave; kh&aacute;ch ch&iacute;nh phủ</strong>&nbsp;- số 12 Ng&ocirc; Quyền, trước l&agrave; phủ kh&acirc;m sai Bắc Kỳ. Sau c&aacute;ch mạng th&aacute;ng 8/1945, ng&ocirc;i nh&agrave; n&agrave;y l&agrave; Bắc Bộ Phủ - trụ sở của ch&iacute;nh phủ nước Việt Nam d&acirc;n chủ cộng h&ograve;a, nơi l&agrave;m việc của Chủ tịch Hồ Ch&iacute; Minh từ th&aacute;ng 8/1945-12/1946. Giờ đ&acirc;y nơi n&agrave;y&nbsp;trở th&agrave;nh một trong những nơi để c&aacute;c c&ocirc; d&acirc;u ch&uacute; rể chụp ảnh cưới b&ecirc;n cạnh vườn hoa con c&oacute;c</p>\r\n			&nbsp;.\r\n\r\n			<p>&nbsp;</p>\r\n			</td>\r\n		</tr>\r\n	</tbody>\r\n</table>\r\n\r\n<table align=\"center\">\r\n	<tbody>\r\n		<tr>\r\n			<td>\r\n			<p><img alt=\"17 diem tham quan khong nen bo qua khi toi Ha Noi hinh anh 9\" src=\"https://znews-photo-td.zadn.vn/w1024/Uploaded/tmuitg/2016_08_29/12_2.jpg\" style=\"height:720px; width:960px\" title=\"17 điểm tham quan không nên bỏ qua khi tới Hà Nội hình ảnh 9\" /></p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n			<p><strong>Th&aacute;p nước H&agrave;ng Đậu</strong>&nbsp;l&agrave; c&ocirc;ng tr&igrave;nh x&acirc;y dựng năm 1894 - thường được người d&acirc;n lầm tưởng gọi l&agrave; Bốt H&agrave;ng Đậu, nằm giữa ng&atilde; 5 sầm uất cạnh vườn hoa H&agrave;ng Đậu. Ng&agrave;y xưa, c&ocirc;ng tr&igrave;nh phủ r&ecirc;u phong, c&oacute; n&eacute;t đẹp ri&ecirc;ng. Khi được tu sửa nh&acirc;n dịp 1.000 năm Thăng Long - H&agrave; Nội, th&aacute;p được kho&aacute;c một tấm &aacute;o mới.</p>\r\n			</td>\r\n		</tr>\r\n	</tbody>\r\n</table>\r\n\r\n<table align=\"center\">\r\n	<tbody>\r\n		<tr>\r\n			<td>\r\n			<p><img alt=\"17 diem tham quan khong nen bo qua khi toi Ha Noi hinh anh 10\" src=\"https://znews-photo-td.zadn.vn/w1024/Uploaded/tmuitg/2016_08_29/14_2.jpg\" style=\"height:720px; width:960px\" title=\"17 điểm tham quan không nên bỏ qua khi tới Hà Nội hình ảnh 10\" /></p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n			<p><strong>Tượng đ&agrave;i L&yacute; Th&aacute;i Tổ</strong>&nbsp;đặt tại Vườn hoa Ch&iacute; Linh (l&agrave; t&ecirc;n gọi kh&aacute;c của Vườn hoa L&yacute; Th&aacute;i Tổ) - người c&oacute; c&ocirc;ng khai lập kinh th&agrave;nh Thăng Long. Vườn hoa nằm tr&ecirc;n đường Đinh Ti&ecirc;n Ho&agrave;ng, quận Ho&agrave;n Kiếm, tại trung t&acirc;m thủ đ&ocirc; H&agrave; Nội, Việt Nam.&nbsp;Tượng được đ&uacute;c bằng đồng nguy&ecirc;n khối, vừa được dựng v&agrave;o năm 2004.</p>\r\n			</td>\r\n		</tr>\r\n	</tbody>\r\n</table>\r\n\r\n<table align=\"center\">\r\n	<tbody>\r\n		<tr>\r\n			<td>\r\n			<p><img alt=\"17 diem tham quan khong nen bo qua khi toi Ha Noi hinh anh 11\" src=\"https://znews-photo-td.zadn.vn/w1024/Uploaded/tmuitg/2016_08_29/15_2.jpg\" style=\"height:720px; width:960px\" title=\"17 điểm tham quan không nên bỏ qua khi tới Hà Nội hình ảnh 11\" /></p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n			<p><strong>Cầu Long Bi&ecirc;n</strong>&nbsp;l&agrave; c&acirc;y cầu th&eacute;p đầu ti&ecirc;n bắc qua s&ocirc;ng Hồng, nối quận Ho&agrave;n Kiếm với quận Long Bi&ecirc;n, do Ph&aacute;p x&acirc;y dựng (1898-1902).<br />\r\n			Buổi tối, l&ecirc;n đ&acirc;y ngắm trăng sao, h&oacute;ng gi&oacute; th&igrave; tuyệt vời. Những ng&agrave;y trời se lạnh, ăn ng&ocirc; nướng, mực nướng tr&ecirc;n cầu th&igrave; khỏi ch&ecirc;.</p>\r\n			</td>\r\n		</tr>\r\n	</tbody>\r\n</table>\r\n\r\n<table align=\"center\">\r\n	<tbody>\r\n		<tr>\r\n			<td>\r\n			<p><img alt=\"17 diem tham quan khong nen bo qua khi toi Ha Noi hinh anh 12\" src=\"https://znews-photo-td.zadn.vn/w1024/Uploaded/tmuitg/2016_08_29/16_2.jpg\" style=\"height:720px; width:960px\" title=\"17 điểm tham quan không nên bỏ qua khi tới Hà Nội hình ảnh 12\" /></p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n			<p><strong>Bảo t&agrave;ng lịch sử Việt Nam</strong>&nbsp;l&agrave; nơi lưu giữ những hiện vật, phản &aacute;nh c&aacute;c nền văn h&oacute;a, lịch sử dựng nước v&agrave; giữ nước của người Việt Nam từ thuở ban đầu khai s&aacute;ng đến ng&agrave;y ra đời nước Việt Nam D&acirc;n chủ Cộng h&ograve;a.<br />\r\n			T&ocirc;i rất th&iacute;ch kiến tr&uacute;c ở đ&acirc;y, nhưng th&uacute; thực chưa v&agrave;o. L&uacute;c chụp tấm ảnh n&agrave;y đ&atilde; muộn qu&aacute;, n&ecirc;n đ&agrave;nh hẹn dịp kh&aacute;c.</p>\r\n			</td>\r\n		</tr>\r\n	</tbody>\r\n</table>\r\n\r\n<table align=\"center\">\r\n	<tbody>\r\n		<tr>\r\n			<td>\r\n			<p><img alt=\"17 diem tham quan khong nen bo qua khi toi Ha Noi hinh anh 13\" src=\"https://znews-photo-td.zadn.vn/w1024/Uploaded/tmuitg/2016_08_29/17_2.jpg\" style=\"height:720px; width:960px\" title=\"17 điểm tham quan không nên bỏ qua khi tới Hà Nội hình ảnh 13\" /></p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n			<p><strong>Nh&agrave; thờ H&agrave;m Long</strong>&nbsp;l&agrave; một trong những nh&agrave; thờ đẹp nhất H&agrave; Nội, với hai mặt tiền nằm tr&ecirc;n c&aacute;c tuyến phố H&agrave;m Long v&agrave; Ng&ocirc; Th&igrave; Nhậm. Đ&acirc;y cũng l&agrave; nơi được nhiều c&ocirc; d&acirc;u ch&uacute; rể lựa chọn tổ chức đ&aacute;m cưới. Nh&agrave; thờ c&oacute; kh&ocirc;ng gian rộng, c&aacute;c họa tiết trang tr&iacute; bằng d&acirc;y thừng kiểu d&acirc;y &aacute;o d&ograve;ng Phanxico, vừa cổ k&iacute;nh vừa hiện đại.<br />\r\n			Đối với nhiều người trẻ, c&oacute; lẽ khu n&agrave;y nổi tiếng với m&oacute;n nộm chim bồ c&acirc;u được b&aacute;n đối diện.</p>\r\n			</td>\r\n		</tr>\r\n	</tbody>\r\n</table>\r\n\r\n<table align=\"center\">\r\n	<tbody>\r\n		<tr>\r\n			<td>\r\n			<p><img alt=\"17 diem tham quan khong nen bo qua khi toi Ha Noi hinh anh 14\" src=\"https://znews-photo-td.zadn.vn/w1024/Uploaded/tmuitg/2016_08_29/18_2.jpg\" style=\"height:687px; width:960px\" title=\"17 điểm tham quan không nên bỏ qua khi tới Hà Nội hình ảnh 14\" /></p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n			<p><strong>Chợ Đồng Xu&acirc;n</strong>&nbsp;l&agrave; một trong những chợ lớn nhất tại H&agrave; Nội, l&agrave; chợ lớn nhất trong khu phố cổ. Chợ c&oacute; lịch sử tồn tại h&agrave;ng trăm năm, từ thời phong kiến nh&agrave; Nguyễn.<br />\r\n			N&oacute;i chung, chợ n&agrave;y chỉ cho kh&aacute;ch mua bu&ocirc;n v&agrave; du kh&aacute;ch nước ngo&agrave;i. T&ocirc;i chưa bao giờ v&agrave;o.&nbsp;</p>\r\n\r\n			<p>&nbsp;</p>\r\n\r\n			<p>&nbsp;</p>\r\n			</td>\r\n		</tr>\r\n	</tbody>\r\n</table>\r\n\r\n<table align=\"center\">\r\n	<tbody>\r\n		<tr>\r\n			<td>\r\n			<p><img alt=\"17 diem tham quan khong nen bo qua khi toi Ha Noi hinh anh 15\" src=\"https://znews-photo-td.zadn.vn/w1024/Uploaded/tmuitg/2016_08_29/20_3.jpg\" style=\"height:720px; width:960px\" title=\"17 điểm tham quan không nên bỏ qua khi tới Hà Nội hình ảnh 15\" /></p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n			<p><strong>Cung Văn ho&aacute; Lao động Hữu nghị</strong>&nbsp;nằm tại 91 phố Trần Hưng Đạo, quận Ho&agrave;n Kiếm, H&agrave; Nội, l&agrave; một c&ocirc;ng tr&igrave;nh kiến tr&uacute;c, văn h&oacute;a d&agrave;nh cho c&aacute;c buổi biểu diễn nghệ thuật, c&aacute;c hội nghị khoa học, triển l&atilde;m... C&ocirc;ng tr&igrave;nh được khởi c&ocirc;ng v&agrave;o ng&agrave;y 1/1/1978 tr&ecirc;n nền cũ của nh&agrave; Đấu Xảo H&agrave; Nội (bị bom ph&aacute; hủy trong Thế chiến lần thứ 2), tới ng&agrave;y 1/9/1985 th&igrave; ho&agrave;n th&agrave;nh.<br />\r\n			T&ocirc;i nhớ nhất những ng&agrave;y tập nhảy hip hop tr&ecirc;n n&agrave;y, chỗ sảnh to c&oacute; l&aacute;t đ&aacute; hoa, tập được một l&uacute;c th&igrave; bị mấy anh bảo vệ ra đuổi.</p>\r\n			</td>\r\n		</tr>\r\n	</tbody>\r\n</table>\r\n\r\n<table align=\"center\">\r\n	<tbody>\r\n		<tr>\r\n			<td>\r\n			<p><img alt=\"17 diem tham quan khong nen bo qua khi toi Ha Noi hinh anh 16\" src=\"https://znews-photo-td.zadn.vn/w1024/Uploaded/tmuitg/2016_08_29/23_1.jpg\" style=\"height:720px; width:960px\" /></p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n			<p><strong>Di t&iacute;ch cột cờ H&agrave; Nội</strong>&nbsp;- hay c&ograve;n gọi Kỳ đ&agrave;i H&agrave; Nội - l&agrave; một kết cấu dạng th&aacute;p được x&acirc;y dựng c&ugrave;ng thời với th&agrave;nh H&agrave; Nội, dưới triều nh&agrave; Nguyễn (bắt đầu năm 1805, ho&agrave;n th&agrave;nh năm 1812). Kiến tr&uacute;c cột cờ bao gồm ba tầng đế v&agrave; một th&acirc;n cột, được coi l&agrave; một trong những biểu tượng của th&agrave;nh phố. C&ocirc;ng tr&igrave;nh nằm tr&ecirc;n mặt đường Điện Bi&ecirc;n Phủ - đối diện c&ocirc;ng vi&ecirc;n Lenin v&agrave; c&ugrave;ng trong khu&ocirc;n vi&ecirc;n Bảo t&agrave;ng Lịch sử Qu&acirc;n sự Việt Nam.</p>\r\n			</td>\r\n		</tr>\r\n	</tbody>\r\n</table>\r\n\r\n<table align=\"center\">\r\n	<tbody>\r\n		<tr>\r\n			<td>\r\n			<p><img alt=\"17 diem tham quan khong nen bo qua khi toi Ha Noi hinh anh 17\" src=\"https://znews-photo-td.zadn.vn/w1024/Uploaded/tmuitg/2016_08_29/14063762_877869315681318_9190235084934697285_n.jpg\" style=\"height:718px; width:960px\" /></p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n			<p><strong>Th&aacute;p R&ugrave;a</strong>&nbsp;ở hồ Gươm.</p>\r\n			</td>\r\n		</tr>\r\n	</tbody>\r\n</table>\r\n', 'http://localhost/tour/admin/uploads/Images/sapaa.jpg', '2018-06-18 16:22:08', b'1'),
(2, 'Những món đồ thiết yếu khi đi du lịch', '<p><strong>1. L&ecirc;n lịch, kiểm tra địa điểm, c&aacute;c điểm chơi v&agrave; t&igrave;nh h&igrave;nh thời tiết</strong></p>\r\n\r\n<p>Một t&uacute;i thuốc nhỏ kh&ocirc;ng bao giờ thừa</p>\r\n\r\n<p>M&aacute;y sấy t&oacute;c gi&uacute;p bạn v&agrave; trẻ rất nhiều</p>\r\n\r\n<p>M&aacute;y ảnh lưu giữ kỉ niệm</p>\r\n\r\n<p>Trang phục gọn g&agrave;ng, thoải m&aacute;i</p>\r\n\r\n<p>K&iacute;nh r&acirc;m tốt cho mắt bạn</p>\r\n\r\n<p>D&eacute;p t&ocirc;ng đi v&agrave;o buổi tối</p>\r\n\r\n<p>Bộ trang điểm gọn nhẹ</p>\r\n\r\n<p>Kim chỉ</p>\r\n\r\n<p>Gi&agrave;y đi lại tiện lợi</p>\r\n\r\n<p>B&agrave;n chải cho b&eacute; y&ecirc;u</p>\r\n\r\n<p>- Đ&acirc;y l&agrave; việc đầu ti&ecirc;n m&agrave; bạn phải nghĩ tới khi chuẩn bị cho một chuyến đi. Biết được c&aacute;c điểm cần đến trong địa phương sẽ gi&uacute;p bạn kh&ocirc;ng qu&aacute; bỡ ngỡ khi đến nơi.</p>\r\n\r\n<p>- Chọn thời điểm trong năm để đi cũng rất quan trọng. Thường c&aacute;c chuyến đi sẽ v&agrave;o m&ugrave;a h&egrave; hay v&agrave;o dịp nghỉ Lễ, Tết. Để đảm bảo, bạn n&ecirc;n đặt ph&ograve;ng kh&aacute;ch sạn trước, li&ecirc;n hệ phương tiện đi lại, mua sẵn v&eacute; t&agrave;u hoặc v&eacute; m&aacute;y bay cho cả hai chiều đi v&agrave; về.</p>\r\n\r\n<p>- T&igrave;m hiểu thời tiết ở nơi n&agrave;y sẽ gi&uacute;p bạn c&oacute; phương &aacute;n mang đồ cần mang theo.</p>\r\n\r\n<p><em>Lưu &yacute;:</em>&nbsp;Tr&aacute;nh đi v&agrave;o m&ugrave;a mưa b&atilde;o hay trong thời tiết qu&aacute; lạnh</p>\r\n\r\n<p><strong>2. Giấy tờ t&ugrave;y th&acirc;n</strong></p>\r\n\r\n<p>- Tiền bạc đầy đủ.</p>\r\n\r\n<p>- Nếu bạn ra nước ngo&agrave;i th&igrave; n&ecirc;n đổi trước đồng tiền của nước đ&oacute; tại nh&agrave; để khi sang c&oacute; thể d&ugrave;ng ngay</p>\r\n\r\n<p>- Hộ chiếu v&agrave; chứng minh thư</p>\r\n\r\n<p>- Bản sao giấy đăng k&yacute; kết h&ocirc;n (để check-in kh&aacute;ch sạn khi đi c&ugrave;ng gia đ&igrave;nh)</p>\r\n\r\n<p>- Bản sao giấy khai sinh của con c&aacute;i (để check-in m&aacute;y bay trong nước khi chưa c&oacute; hộ chiếu, CMND)</p>\r\n\r\n<p>- Số điện thoại khẩn cấp &ndash; địa chỉ của Đại sứ qu&aacute;n Việt Nam (khi tự đi du lịch nước ngo&agrave;i)</p>\r\n\r\n<p>- 2 ảnh c&aacute; nh&acirc;n (khổ l&agrave;m hộ chiếu), bản sao th&ocirc;ng tin c&aacute; nh&acirc;n trong hộ chiếu (sử dụng khi mất hộ chiếu)</p>\r\n\r\n<p>- Bản sao danh s&aacute;ch đồ d&ugrave;ng mang theo (để kiểm tra khi mang về, hoặc khai b&aacute;o khi mất h&agrave;nh l&yacute;)</p>\r\n\r\n<p>- Giấy ph&eacute;p l&aacute;i xe, bảo hiểm (nếu bạn tự l&aacute;i xe đi du lịch)</p>\r\n\r\n<p>- Sổ tay nhỏ ghi ch&eacute;p những địa chỉ, số điện thoại c&oacute; thể li&ecirc;n lạc nếu bạn c&oacute; việc</p>\r\n\r\n<p>Lưu &yacute;: C&aacute;c giấy tờ n&agrave;y bạn n&ecirc;n cất gọn g&agrave;ng trong t&uacute;i đeo b&ecirc;n người để tiện việc tr&igrave;nh b&aacute;o v&agrave; cẩn thận khi ở nơi đ&ocirc;ng người</p>\r\n\r\n<table align=\"center\" border=\"0\" cellpadding=\"5\" cellspacing=\"0\" style=\"width:40px\">\r\n	<tbody>\r\n		<tr>\r\n			<td><img alt=\"8 lưu ý cho một chuyến đi du lịch\" src=\"http://img.news.zing.vn/img/152/t152784.jpg#1529381739057\" /></td>\r\n		</tr>\r\n		<tr>\r\n			<td><img alt=\"8 lưu ý cho một chuyến đi du lịch\" src=\"http://img.news.zing.vn/img/152/t152785.jpg#1529381739059\" style=\"width:400px\" /></td>\r\n		</tr>\r\n	</tbody>\r\n</table>\r\n\r\n<p><strong>3. Đồ điện tử mang theo</strong></p>\r\n\r\n<p>- Điện thoại di động</p>\r\n\r\n<p>- M&aacute;y chụp ảnh, pin, sạc, thẻ nhớ</p>\r\n\r\n<p>- M&aacute;y nghe nhạc Ipod/ Discman/MP3, pin</p>\r\n\r\n<p>- Đồ sạc pin cho điện thoại di động, m&aacute;y chụp ảnh, quay phim, laptop</p>\r\n\r\n<p>- Ổ cắm điện ph&ugrave; hợp hoặc ổ cắm đa quốc gia (khi du lịch nước ngo&agrave;i)</p>\r\n\r\n<p>- M&aacute;y sấy t&oacute;c nhỏ, tiện v&agrave; gọn</p>\r\n\r\n<p><em>Lưu &yacute;:</em>&nbsp;Bạn n&ecirc;n xếp gọn trong t&uacute;i chống thấm nước.</p>\r\n\r\n<p><strong>4. Thuốc men</strong></p>\r\n\r\n<p>- Thuốc ti&ecirc;u h&oacute;a, ti&ecirc;u chảy</p>\r\n\r\n<p>- Thuốc cảm, vi&ecirc;m họng, hạ sốt</p>\r\n\r\n<p>- Kem chống nắng, chống nẻ, chống dị ứng</p>\r\n\r\n<p>- Kem chống c&ocirc;n tr&ugrave;ng</p>\r\n\r\n<p>- Dầu gi&oacute;</p>\r\n\r\n<p>- Vitamin C, B1</p>\r\n\r\n<p>- Băng cứu thương, gel</p>\r\n\r\n<p>- Thuốc chống say xe/m&aacute;y bay/t&agrave;u</p>\r\n\r\n<p>- C&aacute;c loại thuốc đặc trị t&ugrave;y theo nhu cầu của từng người</p>\r\n\r\n<p>- Thuốc cho trẻ</p>\r\n\r\n<p><em>Lưu &yacute;:</em>&nbsp;Bạn n&ecirc;n để nơi dễ lấy trong h&agrave;nh l&yacute;</p>\r\n\r\n<p><strong>5. Trang phục: T&ugrave;y theo số ng&agrave;y v&agrave; nơi bạn đến để mang đồ cho ph&ugrave; hợp</strong></p>\r\n\r\n<p>- Mũ c&oacute; v&agrave;nh v&agrave; c&oacute; thể gấp gọn trong t&uacute;i</p>\r\n\r\n<p>- K&iacute;nh m&aacute;t</p>\r\n\r\n<p>- Bịt mặt</p>\r\n\r\n<p>- &Aacute;o thun thoải m&aacute;i v&agrave; thuận tiện, chất liệu kh&ocirc;ng phải l&agrave; c&agrave;ng tốt</p>\r\n\r\n<p>- Quần d&agrave;i, quần short, v&aacute;y</p>\r\n\r\n<p>- Quần &aacute;o ngủ (pijama)</p>\r\n\r\n<p>- Gi&agrave;y thể thao v&agrave; 1 đ&ocirc;i d&eacute;p</p>\r\n\r\n<p>- Đồ l&oacute;t</p>\r\n\r\n<p>- Tất, găng tay, khăn cho&agrave;ng, &aacute;o ấm, &aacute;o len (nếu đến v&ugrave;ng c&oacute; thời tiết lạnh)</p>\r\n\r\n<p><em>Lưu &yacute;:</em>&nbsp;Quần &aacute;o n&ecirc;n để trong t&uacute;i nilon trước khi cho v&agrave;o vali, tr&aacute;nh m&ugrave;i t&agrave;u xe</p>\r\n\r\n<p><strong>6. Đồ d&ugrave;ng vệ sinh c&aacute; nh&acirc;n:</strong></p>\r\n\r\n<p>- B&agrave;n chải, kem đ&aacute;nh răng, lược, khăn mặt</p>\r\n\r\n<p>- Dầu gội, sữa tắm, sữa rửa mặt</p>\r\n\r\n<p>- Kem v&agrave; đồ d&ugrave;ng cạo r&acirc;u cho nam</p>\r\n\r\n<p>- Dung dịch s&uacute;c miệng</p>\r\n\r\n<p>- Mỹ phẩm: n&ecirc;n mang bằng những lọ nhỏ, gọn</p>\r\n\r\n<p>- Keo xức t&oacute;c</p>\r\n\r\n<p>- Dung dịch tẩy trang</p>\r\n\r\n<p><em>Lưu &yacute;:</em>&nbsp;Mang theo bằng những lọ nhỏ, để ở ngăn ngo&agrave;i của balo hoặc vali</p>\r\n\r\n<p><strong>7. C&aacute;c đồ d&ugrave;ng kh&aacute;c:</strong></p>\r\n\r\n<p>- T&uacute;i đeo ngang vai đựng c&aacute;c đồ cần thiết v&agrave; quan trọng</p>\r\n\r\n<p>- Bal&ocirc;, vali đựng đồ</p>\r\n\r\n<p>- Tạp ch&iacute;, s&aacute;ch giải tr&iacute;</p>\r\n\r\n<p>- Giấy ghi ch&uacute;, viết</p>\r\n\r\n<p>- Bản đồ, s&aacute;ch hướng dẫn c&aacute;c điểm cần đến</p>\r\n\r\n<p>- T&uacute;i kim chỉ, khuy...</p>\r\n\r\n<p>- T&uacute;i nilon đựng đồ bẩn, t&uacute;i n&ocirc;n</p>\r\n\r\n<p>- Bộ b&agrave;i, đ&ocirc;min&ocirc;, cờ tướng</p>\r\n\r\n<p>- N&uacute;t nh&eacute;t tai chống ồn (khi ngủ)/chống v&agrave;o nước (khi bơi)</p>\r\n\r\n<p>- Mặt nạ che mắt khi ngủ</p>\r\n\r\n<p>- Gối ngủ (tr&ecirc;n xe, m&aacute;y bay)</p>\r\n\r\n<p>- Con dao nhỏ để gọt hoa quả</p>\r\n\r\n<p>- Bật lửa cho ch&agrave;ng</p>\r\n\r\n<p>- Một chiếc chăn mỏng</p>\r\n\r\n<p>- Quần &aacute;o đi mưa, &ocirc; hoặc mảnh mưa</p>\r\n\r\n<p>- Đồ ăn vặt: một ch&uacute;t b&aacute;nh tr&aacute;i, nước uống, socola, kẹo caosu</p>\r\n\r\n<p><em>Lưu &yacute;:</em>&nbsp;C&aacute;c vật nhọn khi bạn mang theo, nhớ để trong h&agrave;nh l&yacute; k&iacute; gửi.</p>\r\n\r\n<table align=\"center\" border=\"0\" cellpadding=\"5\" cellspacing=\"0\" style=\"width:40px\">\r\n	<tbody>\r\n		<tr>\r\n			<td><img alt=\"8 lưu ý cho một chuyến đi du lịch\" src=\"http://img.news.zing.vn/img/152/t152786.jpg#1529381739157\" style=\"width:450px\" /></td>\r\n		</tr>\r\n		<tr>\r\n			<td><img alt=\"8 lưu ý cho một chuyến đi du lịch\" src=\"http://img.news.zing.vn/img/152/t152787.jpg#1529381739331\" style=\"width:450px\" /></td>\r\n		</tr>\r\n	</tbody>\r\n</table>\r\n\r\n<p><strong>8. Với trẻ nhỏ:</strong></p>\r\n\r\n<p>- Khăn l&ocirc;ng</p>\r\n\r\n<p>- Khăn giấy ướt (hộp)</p>\r\n\r\n<p>- T&atilde; giấy</p>\r\n\r\n<p>- Mền đắp, quấn (nhẹ, mềm)</p>\r\n\r\n<p>- T&uacute;i địu b&eacute; sau lưng/trước ngực</p>\r\n\r\n<p>- Nhiều quần &aacute;o ngo&agrave;i/l&oacute;t ban ng&agrave;y, ban đ&ecirc;m (để thay đổi), &aacute;o quần ấm, vớ</p>\r\n\r\n<p>- Dầu, phấn trẻ em</p>\r\n\r\n<p>- Đồ chơi/giải tr&iacute; (t&ugrave;y lứa tuổi)</p>\r\n\r\n<p>- Kẹp, d&acirc;y buộc t&oacute;c cho b&eacute; g&aacute;i</p>\r\n\r\n<p>- Sữa, nước tr&aacute;i c&acirc;y đ&oacute;ng hộp, thức ăn trẻ em</p>\r\n\r\n<p>- Hộp sữa, b&igrave;nh sữa, dụng cụ pha sữa, b&igrave;nh h&acirc;m sữa, m&aacute;y khử tr&ugrave;ng đồ d&ugrave;ng ăn uống của b&eacute;</p>\r\n\r\n<p>- Giầy &amp; d&eacute;p</p>\r\n\r\n<p>- &Aacute;o kho&aacute;c (d&ugrave; đến nơi c&oacute; kh&iacute; hậu n&oacute;ng, do m&aacute;y điều h&ograve;a trong nh&agrave; h&agrave;ng c&oacute; thể rất lạnh với b&eacute;)</p>\r\n\r\n<p>- Dầu tắm, gội v&agrave; đồ d&ugrave;ng vệ sinh c&aacute; nh&acirc;n</p>\r\n\r\n<p><em>Lưu &yacute;:</em>&nbsp;Cẩn thận với đồ cho trẻ, mang những thứ cần thiết v&agrave; tiện dụng nhất</p>\r\n\r\n<p>Ch&uacute;c gia đ&igrave;nh bạn c&oacute; một chuyến đi vui vẻ v&agrave; nhiều niềm vui!</p>\r\n', 'http://localhost:81/tour/admin/uploads/Images/chuan-bi-do-khi-di-phuot.jpg', '2018-06-19 00:00:00', b'0'),
(3, '5 món ăn phải thử khi du lịch Đà Nẵng', '<p>&nbsp;<strong>Share</strong></p>\r\n\r\n<p>Đ&agrave; Nẵng được mệnh danh l&agrave; th&agrave;nh phố đ&aacute;ng sống nhất Việt Nam, bạn đ&atilde; bao giờ t&igrave;m hiểu rằng tại sao th&agrave;nh phố xinh đẹp n&agrave;y lại được gọi bằng c&aacute;i t&ecirc;n đ&aacute;ng y&ecirc;u như vậy chưa? Đ&agrave; Nẵng l&agrave; một th&agrave;nh phố xinh đẹp của dải đất Miền Trung, đ&acirc;y l&agrave; th&agrave;nh phố du lịch th&acirc;n thiện với m&ocirc;i trường. Th&agrave;nh phố n&agrave;y nổi tiếng với Chương tr&igrave;nh &ldquo;5 kh&ocirc;ng&rdquo;: kh&ocirc;ng c&oacute; hộ đ&oacute;i, kh&ocirc;ng c&oacute; người m&ugrave; chữ, kh&ocirc;ng c&oacute; người lang thang xin ăn, kh&ocirc;ng c&oacute; người nghiện ma t&uacute;y trong cộng đồng, kh&ocirc;ng c&oacute; giết người cướp. Y&ecirc;n b&igrave;nh, xanh sạch đẹp, văn minh với nhiều địa điểm du lịch th&uacute; vị, ẩm thực hấp dẫn, bạn c&ograve;n mong chờ g&igrave; hơn nữa ở th&agrave;nh phố n&agrave;y. Đến với Đ&agrave; Nẵng bạn v&agrave; gia đ&igrave;nh h&atilde;y y&ecirc;n t&acirc;m v&agrave; thoải m&aacute;i cảm nhận vẻ đẹp phong cảnh, sự th&acirc;n thiện, h&ograve;a đồng của con người v&agrave; nhất l&agrave; những m&oacute;n &ldquo;đặc sản&rdquo; nơi đ&acirc;y. Nếu bạn l&agrave; fan bự của ẩm thực th&igrave; bạn cứ y&ecirc;n t&acirc;m l&agrave; đồ ăn ở Đ&agrave; Nẵng đ&uacute;ng theo ti&ecirc;u ch&iacute; ngon &ndash; bổ &ndash; rẻ, nhất định kh&ocirc;ng sợ &ldquo;lỗ&rdquo;.<a href=\"https://hi.com.vn/wp-content/uploads/2016/07/5-mon-an-phai-thu-khi-du-lich-da-nang-de.jpg\" rel=\"prettyPhoto\"><img alt=\"5-mon-an-phai-thu-khi-du-lich-da-nang de\" src=\"https://hi.com.vn/wp-content/uploads/2016/07/5-mon-an-phai-thu-khi-du-lich-da-nang-de.jpg\" style=\"height:2440px; width:4154px\" /></a></p>\r\n\r\n<p>H&ocirc;m nay&nbsp;<strong>hi.com.vn</strong>&nbsp;sẽ giới thiệu đến bạn những m&oacute;n ăn phải thử khi du lịch Đ&agrave; Nẵng. Bạn n&ecirc;n note lại để gi&agrave;nh cho chuyến du lịch của m&igrave;nh nh&eacute;. N&agrave;o, ch&uacute;ng ta c&ugrave;ng kh&aacute;m ph&aacute; th&ocirc;i</p>\r\n\r\n<ol>\r\n	<li><strong>M&igrave; Quảng</strong></li>\r\n</ol>\r\n\r\n<p>Đến Đ&agrave; Nẵng cũng như những địa phương kh&aacute;c của Miền Trung bạn n&ecirc;n thưởng thức M&igrave; Quảng đầu ti&ecirc;n để cảm nhận được sự kh&aacute;c biệt của hương vị m&igrave; mỗi v&ugrave;ng. M&igrave; Quảng l&agrave; m&oacute;n ăn nổi tiếng của Đ&agrave; Th&agrave;nh. Đặc trưng của M&igrave; Quảng Đ&agrave; Nẵng l&agrave; những cọng m&igrave; d&agrave;y, cứng v&agrave; to th&ocirc;. M&igrave; Quảng kh&ocirc;ng c&oacute; c&ocirc;ng thức cố định m&agrave; giống như m&oacute;n lẩu, bạn c&oacute; rất nhiều c&aacute;ch thưởng thức m&oacute;n ăn n&agrave;y. M&igrave; Quảng c&oacute; rất nhiều m&oacute;n như m&igrave; Quảng sườn non, m&igrave; Quảng lươn, m&igrave; Quảng c&aacute; l&oacute;c, m&igrave; Quảng chả cua&hellip; nhưng nổi tiếng nhất v&agrave; truyền thống nhất l&agrave; m&igrave; Quảng t&ocirc;m, g&agrave;, trứng, thịt. Bạn c&oacute; biết th&agrave;nh phần kh&ocirc;ng thể thiếu của m&igrave; Quảng l&agrave; g&igrave; kh&ocirc;ng? Đ&oacute; ch&iacute;nh l&agrave; đậu phộng rang v&agrave; b&aacute;nh tr&aacute;ng m&egrave; nướng gi&ograve;n.</p>\r\n\r\n<p><a href=\"https://hi.com.vn/wp-content/uploads/2016/07/5-mon-an-phai-thu-khi-du-lich-da-nang-d.jpg\" rel=\"prettyPhoto\"><img alt=\"5-mon-an-phai-thu-khi-du-lich-da-nang d\" src=\"https://hi.com.vn/wp-content/uploads/2016/07/5-mon-an-phai-thu-khi-du-lich-da-nang-d.jpg\" style=\"height:330px; width:500px\" /></a></p>\r\n\r\n<p>C&aacute;ch thưởng thức M&igrave; Quảng đ&oacute; l&agrave; trộn đều tất cả nguy&ecirc;n liệu v&agrave;o một t&ocirc; sau đ&oacute; chan phần nước l&egrave;o được ninh từ xương heo ngon c&ugrave;ng phần t&ocirc;m gi&atilde; lấy nước, c&oacute; th&ecirc;m hạt điều tạo n&ecirc;n chất nước s&aacute;nh v&agrave; cho m&agrave;u đẹp mắt. M&igrave; Quảng ăn k&egrave;m với nhiều loại rau sống như cải, x&agrave; l&aacute;ch tươi, bắp chuối sắt mỏng, h&uacute;ng quế, rau răm, gi&aacute; đỗ, ng&ograve; r&iacute; tất cả trộn đều, h&ograve;a quyện với nhau tạo n&ecirc;n m&ugrave;i vị đậm đ&agrave; kh&oacute; qu&ecirc;n.</p>\r\n\r\n<p>Bạn c&oacute; thể ăn M&igrave; Quảng ở mọi nơi tr&ecirc;n đất Đ&agrave; Nẵng nhưng nếu muốn thưởng thức m&oacute;n ăn đ&uacute;ng chất Đ&agrave; Nẵng bạn c&oacute; thể đến đường Ho&agrave;ng Diệu, Phan Thanh, Trưng Nữ Vương, Ho&agrave;ng Hoa Th&aacute;m b&aacute;n buổi s&aacute;ng tới tầm 9h &ndash; 10h v&agrave; một số nơi b&aacute;n cả ng&agrave;y như tr&ecirc;n đường Nguyễn Hữu Thọ, Nguyễn C&ocirc;ng Trứ.</p>\r\n\r\n<p>Gi&aacute; của m&oacute;n M&igrave; Quảng dao động t&ugrave;y nơi, t&ugrave;y t&ocirc; lớn nhỏ khoảng 13.000 &ndash; 25.000 đồng/t&ocirc;, b&aacute;nh tr&aacute;ng b&aacute;n k&egrave;m 3.000 đồng/c&aacute;i. Rất rẻ phải kh&ocirc;ng? Ẩm thực Đ&agrave; Nẵng ngon v&agrave; rẻ m&agrave;, tin t&ocirc;i đi. C&aacute;c bạn n&ecirc;n vận động thật nhiều để ti&ecirc;u bớt phần M&igrave; Quảng v&igrave; ch&uacute;ng t&ocirc;i sẽ giới thiệu đến bạn m&oacute;n mới ngay đ&acirc;y.</p>\r\n\r\n<ol start=\"2\">\r\n	<li><strong>B&uacute;n chả c&aacute;</strong></li>\r\n</ol>\r\n\r\n<p>Thực ra, cứ đến miền Trung l&agrave; đ&acirc;u đ&acirc;u cũng dễ t&igrave;m ra qu&aacute;n b&aacute;n b&uacute;n chả c&aacute; nhưng b&uacute;n chả c&aacute; ở Đ&agrave; Nẵng cũng rất đặc biệt. B&uacute;n chả c&aacute; ngon l&agrave; khi ăn kh&ocirc;ng c&oacute; m&ugrave;i tanh của c&aacute;, chỉ nghe hương thơm của c&aacute;c loại gia vị v&agrave; c&aacute; quyện với nhau ng&agrave;o ngạt. Vị đậm đ&agrave; của t&ocirc; b&uacute;n kết hợp với vị ngọt thanh của rau củ quả v&agrave; vị đậm đ&agrave; của nước l&egrave;o khiến bất cứ thực kh&aacute;ch n&agrave;o cũng phải xu&yacute;t xoa.</p>\r\n\r\n<p><a href=\"https://hi.com.vn/wp-content/uploads/2016/07/5-mon-an-phai-thu-khi-du-lich-da-nang-dr.jpg\" rel=\"prettyPhoto\"><img alt=\"5-mon-an-phai-thu-khi-du-lich-da-nang dr\" src=\"https://hi.com.vn/wp-content/uploads/2016/07/5-mon-an-phai-thu-khi-du-lich-da-nang-dr.jpg\" style=\"height:381px; width:550px\" /></a></p>\r\n\r\n<p>B&uacute;n chả c&aacute; Đ&agrave; Nẵng ăn k&egrave;m với rau sống như gi&aacute; đỗ sống, x&agrave; l&aacute;ch, hung quế v&agrave; đặc biệt, gia vị kh&ocirc;ng thể thiếu đối với m&oacute;n b&uacute;n chả c&aacute; đ&oacute; l&agrave; ớt tỏi gi&atilde; v&agrave; h&agrave;nh ng&acirc;m giấm đường. Vị chua chua ngọt ngọt của h&agrave;nh hương cộng với vị cay của ớt tỏi khiến người ăn c&oacute; những trải nghiệm th&uacute; vị kh&ocirc;ng thể n&agrave;o qu&ecirc;n.</p>\r\n\r\n<ol start=\"3\">\r\n	<li><strong>Gỏi c&aacute; Nam &Ocirc;</strong></li>\r\n</ol>\r\n\r\n<p>Cũng như tr&aacute;i sầu ri&ecirc;ng của miền Nam, m&oacute;n gỏi c&aacute; rất k&eacute;n người ăn nhưng ăn rồi l&agrave; dễ nghiện. &Iacute;t người d&aacute;m thử m&oacute;n n&agrave;y bởi n&oacute; l&agrave;m từ c&aacute; sống. Gỏi c&aacute; Nam &Ocirc; c&oacute; thể được chế biến từ c&aacute; tớp, c&aacute; cơm hoặc c&aacute; m&ograve;i,&hellip; nhưng ngon v&agrave; th&iacute;ch hợp nhất l&agrave; c&aacute; tr&iacute;ch. C&aacute; tr&iacute;ch cỡ lớn hơn ng&oacute;n tay, cắt bỏ đầu, đu&ocirc;i, bụng, lọc bỏ xương, t&aacute;ch th&acirc;n l&agrave;m hai v&agrave; xắt từng miếng nhỏ, &eacute;p nước để r&aacute;o rồi đem ướp với gừng, riềng, tỏi băm nhuyễn v&agrave; th&iacute;nh.</p>\r\n\r\n<p>RELATED POSTS</p>\r\n\r\n<p><a href=\"https://hi.com.vn/am-thuc/kinh-nghiem-du-lich-tam-dao.html\">Kinh nghiệm du lịch Tam Đảo</a></p>\r\n\r\n<p><a href=\"https://hi.com.vn/am-thuc/trang-website-mang-xa-hoi-du-lich-hang-dau.html\">Trang website mạng x&atilde; hội du lịch h&agrave;ng đầu</a></p>\r\n\r\n<p><a href=\"https://hi.com.vn/wp-content/uploads/2016/07/5-mon-an-phai-thu-khi-du-lich-da-nang-s.jpg\" rel=\"prettyPhoto\"><img alt=\"5-mon-an-phai-thu-khi-du-lich-da-nang s\" src=\"https://hi.com.vn/wp-content/uploads/2016/07/5-mon-an-phai-thu-khi-du-lich-da-nang-s.jpg\" style=\"height:446px; width:595px\" /></a></p>\r\n\r\n<p>Nước cốt c&aacute; được đun s&ocirc;i, h&ograve;a th&ecirc;m với nước mắm Nam &Ocirc;, ớt, bột năng, bột ngọt tạo th&agrave;nh thứ nước chấm đặc trưng cho ri&ecirc;ng m&oacute;n gỏi. C&aacute;c m&oacute;n ăn của Đ&agrave; Nẵng đều được ăn kem với nhiều loại rau sống. Rau ăn k&egrave;m với gỏi c&aacute; Nam &Ocirc; rất đa dạng v&agrave; đặc biệt, l&agrave; đọt non của c&aacute;c loại c&oacute;c rừng, tim lan, l&agrave;nh ngạnh, l&aacute; tr&acirc;m, l&aacute; dừng&hellip; vốn chỉ mọc tr&ecirc;n tr&ecirc;n đ&egrave;o Hải V&acirc;n. Tuy nhi&ecirc;n, giờ đ&acirc;y, c&aacute;c m&oacute;n rau kh&aacute;c như dưa chuột, xo&agrave;i, chuối&hellip; được d&ugrave;ng nhiều để đ&aacute;p ứng nhu cầu ng&agrave;y c&agrave;ng tăng của thực kh&aacute;ch.</p>\r\n\r\n<p>Ăn Gỏi c&aacute; Nam &Ocirc; th&igrave; phải tới khu vực Nam &Ocirc; để thưởng thức mới đ&uacute;ng điệu. Hoặc nếu kh&ocirc;ng, bạn c&oacute; thể qua Nguyễn Tất Th&agrave;nh hay cầu Nam &Ocirc; v&agrave; gọi một phần với gi&aacute; từ 40.000 đồng.</p>\r\n\r\n<ol start=\"4\">\r\n	<li><strong>B&aacute;nh tr&aacute;ng thịt heo</strong></li>\r\n</ol>\r\n\r\n<p>Nếu m&oacute;n ăn n&agrave;o đơn giản nhất chắc c&oacute; lẽ l&agrave; m&oacute;n n&agrave;y, B&aacute;nh tr&aacute;ng cuốn thịt heo. Kh&ocirc;ng đ&ograve;i hỏi chế biến cầu k&igrave;, B&aacute;nh tr&aacute;ng cuốn thịt heo đơn giản v&agrave; rất dễ ăn. M&oacute;n Thịt heo cuốn b&aacute;nh tr&aacute;ng ở Đ&agrave; Nẵng nổi tiếng v&igrave; nguy&ecirc;n liệu được chọn kỹ c&agrave;ng, khiến hương vị ho&agrave;n hảo. Thịt heo chỉ lấy phần m&ocirc;ng hoặc vai, sau đ&oacute; đem hấp hơi để giữ nguy&ecirc;n vị ngọt. Rau đều thuộc loại th&ocirc;ng dụng, rất dễ t&igrave;m nhưng phải đảm bảo tươi xanh, kh&ocirc;ng h&eacute;o &uacute;a gồm x&agrave; l&aacute;ch, h&uacute;ng quế, diếp c&aacute;, h&agrave;nh l&aacute;, rau thơm, rau đắng, gi&aacute;, b&uacute;p chuối trắng, dưa leo, chuối ch&aacute;t&hellip;</p>\r\n\r\n<p><a href=\"https://hi.com.vn/wp-content/uploads/2016/07/5-mon-an-phai-thu-khi-du-lich-da-nang.jpg\" rel=\"prettyPhoto\"><img alt=\"5-mon-an-phai-thu-khi-du-lich-da-nang\" src=\"https://hi.com.vn/wp-content/uploads/2016/07/5-mon-an-phai-thu-khi-du-lich-da-nang.jpg\" style=\"height:313px; width:639px\" /></a></p>\r\n\r\n<p>Gia vị quyết định độ ngon của m&oacute;n n&agrave;y ch&iacute;nh l&agrave; mắm n&ecirc;m, loại nước chấm kh&ocirc;ng thể thay thế. Qủa vậy, kh&oacute; thể n&agrave;o từ chối c&aacute;i dai dai của b&aacute;nh tr&aacute;ng kết hợp với vị mềm mại của m&igrave; ướt, th&ecirc;m ch&uacute;t ngọt sắc của thịt, vị tươi m&aacute;t của rau v&agrave; cay nồng n&agrave;n của mắm n&ecirc;m.</p>\r\n\r\n<p>Để tr&ograve;n vị, bạn n&ecirc;n chọn qu&aacute;n tr&ecirc;n đường Ch&acirc;u Thị Vĩnh Tế hoặc Hải Ph&ograve;ng, Duy T&acirc;n, L&ecirc; Duẩn, Đỗ Th&uacute;c Thịnh&hellip; t&ugrave;y nh&agrave; h&agrave;ng sang trọng hay b&igrave;nh d&acirc;n với gi&aacute; khoảng từ 30.000 &ndash; 80.000 đồng.</p>\r\n\r\n<ol start=\"5\">\r\n	<li><strong>B&aacute;nh x&egrave;o</strong></li>\r\n</ol>\r\n\r\n<p>Nếu nghiền c&aacute;c m&oacute;n ăn vặt bạn n&ecirc;n ăn B&aacute;nh x&egrave;o. B&aacute;nh x&egrave;o Đ&agrave; Nẵng kh&ocirc;ng nhỏ qu&aacute; v&agrave; cũng kh&ocirc;ng lớn qu&aacute;, chỉ ri&ecirc;ng một k&iacute;ch cỡ vừa ăn. B&aacute;nh x&egrave;o ở Đ&agrave; Nẵng đặt biệt bởi n&oacute; được l&agrave;m tự bột gạo xay c&oacute; th&ecirc;m l&ograve;ng đỏ trứng v&agrave; bột nghệ, đ&uacute;c tr&ecirc;n chảo n&oacute;ng. Nh&acirc;n b&aacute;nh cũng được lựa kỹ, chỉ l&agrave;m từ t&ocirc;m c&ograve;n sống, thịt ba chỉ nửa nạc nửa mỡ v&agrave; gi&aacute; đỗ tươi. Rau sống sạch sẽ,&nbsp; gồm x&agrave; l&aacute;ch, h&uacute;ng quế, chuối ch&aacute;t, rau cải con&hellip; Nước tương pha chế từ gan heo v&agrave; đậu phụng xay tạo th&agrave;nh một loại nước chấm c&oacute; hương vị b&ugrave;i b&ugrave;i, b&eacute;o b&eacute;o, b&ecirc;n cạnh một ch&eacute;n nước mắm ớt tỏi pha theo kiểu truyền thống.</p>\r\n\r\n<p><a href=\"https://hi.com.vn/wp-content/uploads/2016/07/5-mon-an-phai-thu-khi-du-lich-da-nang-sc.jpg\" rel=\"prettyPhoto\"><img alt=\"5-mon-an-phai-thu-khi-du-lich-da-nang sc\" src=\"https://hi.com.vn/wp-content/uploads/2016/07/5-mon-an-phai-thu-khi-du-lich-da-nang-sc.jpg\" style=\"height:357px; width:597px\" /></a></p>\r\n\r\n<p>B&aacute;nh x&egrave;o ăn n&oacute;ng, quấn trong b&aacute;nh tr&aacute;ng mỏng hoặc l&aacute; cải to. Từng miếng b&aacute;nh khi v&agrave;o trong miệng c&ograve;n thấy được độ gi&ograve;n vừa, th&ecirc;m m&ugrave;i b&eacute;o ngậy, vị ngọt của t&ocirc;m v&agrave; thịt với man m&aacute;t c&aacute;c loại rau h&ograve;a ch&uacute;t ch&aacute;t ch&aacute;t của chuối xanh. Ăn một lần rồi nhớ m&atilde;i. Gi&aacute; b&agrave;nh x&egrave;o cũng &ldquo;mềm&rdquo;, chỉ từ 5.000 đồng/c&aacute;i tr&ecirc;n đường Ho&agrave;ng Diệu, Hải Ph&ograve;ng&hellip;</p>\r\n\r\n<p>Đ&agrave; Nẵng quả thực l&agrave; một thi&ecirc;n đường ẩm thực, nếu kể hết những m&oacute;n ngon của đ&aacute;t n&agrave;y chắc phải mất 1 cuốn s&aacute;ch d&agrave;y. B&agrave;i viết của ch&uacute;ng t&ocirc;i chỉ l&agrave; một gợi &yacute; , nếu bạn thương Đ&agrave; Nẵng h&atilde;y đến chơi ^^ v&agrave; thưởng thức những m&oacute;n ngon ở đ&acirc;y nh&eacute;. Sau khi thưởng thức nhớ chia sẻ c&ugrave;ng ch&uacute;ng t&ocirc;i nh&eacute;</p>\r\n\r\n<p><a href=\"https://hi.com.vn/wp-content/uploads/2016/07/5-mon-an-phai-thu-khi-du-lich-da-nang-ds.jpg\" rel=\"prettyPhoto\"><img alt=\"5-mon-an-phai-thu-khi-du-lich-da-nang ds\" src=\"https://hi.com.vn/wp-content/uploads/2016/07/5-mon-an-phai-thu-khi-du-lich-da-nang-ds.jpg\" style=\"height:325px; width:627px\" /></a></p>\r\n\r\n<p>Đ&agrave; Nẵng l&agrave; trung t&acirc;m của 3 di sản văn h&oacute;a thế giới: phố cổ Hội An, Th&aacute;nh địa Mỹ Sơn, Cố đ&ocirc; Huế cũng như nhiều địa điểm dua lịch nổi tiếng kh&aacute;c như s&ocirc;ng H&agrave;n thơ mộng, B&agrave; N&agrave; Hill, B&aacute;n Đảo Sơn Tr&agrave;, ngũ h&agrave;nh sơn, đ&egrave;o Hải V&acirc;n, b&atilde;i biển Mỹ Kh&ecirc;, Bải Bụt, Gềnh B&agrave;ng&hellip; Đ&agrave; Th&agrave;nh vừa th&iacute;ch hợp cho du kh&aacute;ch th&iacute;ch nghỉ biển, tắm nắng, lại vừa thỏa m&atilde;n những người đam m&ecirc; n&uacute;i cao. Đ&oacute; l&agrave; một điểm đến nhiều lợi &iacute;ch v&agrave; kh&aacute; th&uacute; vị cho h&egrave; n&agrave;y. Bạn h&atilde;y kh&aacute;m ph&aacute; thật nhiều, ăn thật no&nbsp;</p>\r\n', 'http://localhost:81/tour/admin/uploads/Images/5-mon-an-phai-thu-khi-du-lich-da-nang-de.jpg', '2018-06-19 00:00:00', b'0'),
(4, 'Đăng ảnh vé máy bay lên mạng: nguy hiểm rình rập', '<p><strong>Bạn đ&atilde; nh&igrave;n thấy bạn b&egrave; của m&igrave;nh hoặc ch&iacute;nh m&igrave;nh đ&atilde; c&oacute; những bức ảnh chụp&nbsp;<a href=\"https://hi.com.vn/tag/ve-may-bay\">v&eacute; m&aacute;y bay</a>&nbsp;v&agrave; post l&ecirc;n c&aacute;c trang mạng x&atilde; hội. C&oacute; thể chỉ l&agrave; bạn v&ocirc; t&igrave;nh hoặc qu&aacute; h&aacute;o hức cho chuyến đi m&agrave; chưa lường được những nguy hiểm của việc&nbsp;<a href=\"https://hi.com.vn/cam-nang/dang-anh-ve-may-bay-len-mang-nguy-hiem-rinh-rap.html\">đăng ảnh v&eacute; m&aacute;y bay l&ecirc;n mạng</a>.</strong></p>\r\n\r\n<h3>Những nguy cơ kh&oacute; lường</h3>\r\n\r\n<p><a href=\"https://hi.com.vn/wp-content/uploads/2016/05/dang-anh-ve-may-bay-len-mang-2.jpg\" rel=\"prettyPhoto\"><img alt=\"dang anh ve may bay len mang 2\" src=\"https://hi.com.vn/wp-content/uploads/2016/05/dang-anh-ve-may-bay-len-mang-2.jpg\" style=\"height:444px; width:630px\" /></a></p>\r\n\r\n<p>V&eacute; m&aacute;y bay chứa c&aacute;c th&ocirc;ng tin về bạn như t&ecirc;n tuổi, ng&agrave;y sinh, số CMND, ng&agrave;y sinh, địa chỉ&hellip; Những th&ocirc;ng tin n&agrave;y gi&uacute;p cho qu&aacute; tr&igrave;nh l&agrave;m&nbsp;<strong>thủ tục đi m&aacute;y bay trong nước (https://hi.com.vn/ve-may-bay-gia-re/thu-tuc-di-may-bay-trong-nuoc.html)&nbsp;</strong>n&oacute;i ri&ecirc;ng v&agrave; đi m&aacute;y bay n&oacute;i chung được dễ d&agrave;ng nhưng nếu bạn khoe tr&ecirc;n c&aacute;c mạng x&atilde; hội sẽ tạo điều kiện cho những kẻ xấu lợi dụng.</p>\r\n\r\n<p>Những th&ocirc;ng tin c&aacute; nh&acirc;n tr&ecirc;n&nbsp;v&eacute; m&aacute;y bay như t&ecirc;n tuổi, m&atilde; vạch tr&ecirc;n v&eacute; nếu để lộ, kẻ gian sẽ sử dụng để t&igrave;m ra t&agrave;i khoản v&eacute; m&aacute;y bay v&agrave; thay đổi lịch bay m&agrave; bạn kh&ocirc;ng thể biết được. Hiện nay, việc đọc m&atilde; vạch v&eacute; m&aacute;y bay rất dễ d&agrave;ng v&igrave; đ&atilde; c&oacute; c&ocirc;ng cụ đọc m&atilde; vạch miễn ph&iacute;. Ngo&agrave;i ra, nếu bạn để lộ th&ocirc;ng tin c&aacute; nh&acirc;n, tội phạm c&oacute; thể hack t&agrave;i khoản thẻ t&iacute;n dụng ở c&aacute;c ng&acirc;n h&agrave;ng hoặc sử dụng th&ocirc;ng tin c&aacute; nh&acirc;n của bạn phục vụ mục đ&iacute;ch l&agrave;m giả giấy tờ, chiếm đoạt t&agrave;i sản.</p>\r\n\r\n<p>Ngo&agrave;i c&aacute;c mục đ&iacute;ch tr&ecirc;n, kẻ gian c&oacute; thể lấy th&ocirc;ng tin từ số CMND để truy ra m&atilde; số thuế c&aacute; nh&acirc;n, địa chỉ l&agrave;m, biết số điện thoại để l&agrave;m phiền, quảng c&aacute;o sản phẩm, dịch vụ, gửi tin nhắn r&aacute;c&hellip;</p>\r\n\r\n<p><a href=\"https://hi.com.vn/wp-content/uploads/2016/05/dang-anh-ve-may-bay-len-mang.jpg\" rel=\"prettyPhoto\"><img alt=\"dang anh ve may bay len mang\" src=\"https://hi.com.vn/wp-content/uploads/2016/05/dang-anh-ve-may-bay-len-mang.jpg\" style=\"height:388px; width:689px\" /></a></p>\r\n\r\n<h3>Tiếp tay cho c&aacute;c tội phạm</h3>\r\n\r\n<p>Nếu bạn khoe&nbsp;<a href=\"https://hi.com.vn/ve-may-bay-gia-re\"><strong>v&eacute; m&aacute;y bay gi&aacute; rẻ</strong></a>&nbsp;l&ecirc;n mạng, kẻ gian c&oacute; thể sẽ biết lịch tr&igrave;nh, địa điểm đi, địa điểm bạn đến, thời gian đi v&agrave; về v&agrave; ch&uacute;ng sẽ l&ecirc;n kế hoạch lừa đảo, trộm t&agrave;i sản nh&agrave; bạn, bắt c&oacute;c, tống tiền người th&acirc;n của bạn&hellip; V&igrave; vậy, bạn kh&ocirc;ng n&ecirc;n đăng ảnh v&eacute; m&aacute;y bay</p>\r\n\r\n<p>Như đ&atilde; n&oacute;i ở tr&ecirc;n, khi biết th&ocirc;ng tin của bạn, kẻ xấu c&oacute; thể l&agrave;m giả giấy tờ, l&agrave;m giả bằng l&aacute;i xe, l&agrave;m giả thẻ ng&acirc;n h&agrave;ng&hellip; Những thứ n&agrave;y g&acirc;y bất lợi cho bạn, khiến bạn rơi v&agrave;o những vấn đề xấu từ tr&ecirc;n trời rơi xuống m&agrave; bạn kh&ocirc;ng hề biết l&yacute; do của vấn đề đ&oacute;.</p>\r\n\r\n<p>Ngo&agrave;i ra, nếu kẻ xấu biết qu&aacute; nhiều th&ocirc;ng tin của bạn, họ sẽ gọi điện l&ecirc;n tổng đ&agrave;i đổi giờ bay, đổi lịch tr&igrave;nh bay hoặc hủy chuyến bay của bạn. C&aacute;c h&atilde;ng h&agrave;ng kh&ocirc;ng sẽ c&oacute; những quy định nhằm x&aacute;c nhận lại th&ocirc;ng tin h&agrave;nh kh&aacute;ch nhưng c&oacute; thể kh&ocirc;ng tr&aacute;nh khỏi những rủi ro kh&oacute; lường.</p>\r\n\r\n<h3>Lời khuy&ecirc;n d&agrave;nh cho h&agrave;nh kh&aacute;ch</h3>\r\n\r\n<p><a href=\"https://hi.com.vn/wp-content/uploads/2016/05/giay-to-tuy-than-di-may-bay.jpg\" rel=\"prettyPhoto\"><img alt=\"giay to tuy than di may bay\" src=\"https://hi.com.vn/wp-content/uploads/2016/05/giay-to-tuy-than-di-may-bay.jpg\" style=\"height:500px; width:750px\" /></a></p>\r\n\r\n<p>Một lời khuy&ecirc;n từ c&aacute;c chuy&ecirc;n gia d&agrave;nh cho bạn l&agrave; kh&ocirc;ng n&ecirc;n đăng ảnh v&eacute; m&aacute;y bay n&oacute;i ri&ecirc;ng v&agrave; c&aacute;c&nbsp;<a href=\"https://hi.com.vn/ve-may-bay-gia-re/cac-loai-giay-to-tuy-than-can-co-khi-di-may-bay.html\"><strong>giấy tờ t&ugrave;y th&acirc;n đi m&aacute;y bay</strong></a>&nbsp;kh&aacute;c như chứng minh thư nh&acirc;n d&acirc;n, hộ chiếu&hellip; Bất kỳ những loại giấy tờ c&oacute; chứa th&ocirc;ng tin c&aacute; nh&acirc;n của bạn hoặc người th&acirc;n của bạn đều kh&ocirc;ng n&ecirc;n khoe. Mạng x&atilde; hội l&agrave; nơi hỗn độn v&agrave; kẻ xấu sẽ lợi dụng điều đ&oacute;.</p>\r\n\r\n<p>Nếu bạn kh&ocirc;ng thể ngừng lại sự h&aacute;o hức cho chuyến đi v&agrave; muốn khoe v&eacute; m&aacute;y bay th&igrave; n&ecirc;n l&agrave;m mờ hoặc che đi c&aacute;c th&ocirc;ng tin quan trọng như họ t&ecirc;n, ng&agrave;y sinh, số CMND, m&atilde; vạch, địa chỉ&hellip; Đ&oacute; l&agrave; c&aacute;ch bảo mật th&ocirc;ng tin tốt nhất cho bạn. Nhưng bạn n&ecirc;n hạn chế việc n&agrave;y.</p>\r\n\r\n<p>Tr&ecirc;n đ&acirc;y l&agrave; những ch&uacute; &yacute; cho bạn khi sở hữu trong tay tấm&nbsp;<a href=\"https://hi.com.vn/ve-may-bay-gia-re/ve-may-bay-dien-tu.html\"><strong>v&eacute; m&aacute;y bay điện tử</strong></a>&nbsp;hay tấm v&eacute; m&aacute;y bay bằng giấy th&igrave; đều cần ch&uacute; &yacute; kh&ocirc;ng n&ecirc;n đăng ảnh ch&uacute;ng l&ecirc;n mạng. Điều n&agrave;y sẽ tr&aacute;nh cho bạn những rắc rối kh&ocirc;ng đ&aacute;ng c&oacute;.</p>\r\n', 'http://localhost:81/tour/admin/uploads/Images/dang-anh-ve-may-bay-len-mang-1.jpg', '2018-06-19 00:00:00', b'0');
INSERT INTO `tintuc` (`Id`, `TenTinTuc`, `NoiDung`, `HinhAnh`, `NgayTao`, `Xoa`) VALUES
(5, 'Những kiểu ảnh được ưa thích khi đi du lịch', '<p>&nbsp;<strong>Share</strong></p>\r\n\r\n<p><strong>B&acirc;y giờ, &ldquo;qu&agrave; đ&acirc;u&rdquo; đ&atilde; nhường ng&ocirc;i cho &ldquo;ảnh đ&acirc;u&rdquo; trở th&agrave;nh c&acirc;u hỏi được hỏi nhiều nhất sau khi người ta đi du lịch về. M&agrave; cũng phải th&ocirc;i, một chuyến du lịch m&agrave; kh&ocirc;ng c&oacute; những kiểu ảnh đẹp l&agrave;m bạn ưng &yacute; th&igrave; quả thật cũng rất đang tiếc phải kh&ocirc;ng n&agrave;o. Đ&atilde; đi du lịch th&igrave; đừng qu&ecirc;n chụp lại những bức ảnh để ghi dấu lại những khoảnh khắc th&uacute; vị, những cảnh đẹp được ngắm nh&igrave;n. H&atilde;y thử ngh&iacute;a qua&nbsp;<a href=\"https://hi.com.vn/cam-nang/nhung-kieu-anh-duoc-ua-thich-khi-di-du-lich.html\">những kiểu ảnh được ưa th&iacute;ch khi đi du lịch</a>&nbsp;m&agrave;&nbsp;<em>hi.com.vn&nbsp;</em>tổng hợp dưới đ&acirc;y, để xem c&oacute; bao nhi&ecirc;u kiểu gặp gỡ &yacute; tưởng của bạn v&agrave; liệu c&oacute; kiểu n&agrave;o cho bạn &yacute; tưởng mới kh&ocirc;ng nh&eacute;.&nbsp;</strong><a href=\"https://hi.com.vn/wp-content/uploads/2016/07/nhung-kieu-anh-duoc-ua-thich-khi-di-du-lich.jpg\" rel=\"prettyPhoto\"><img alt=\"nhung-kieu-anh-duoc-ua-thich-khi-di-du-lich\" src=\"https://hi.com.vn/wp-content/uploads/2016/07/nhung-kieu-anh-duoc-ua-thich-khi-di-du-lich.jpg\" style=\"height:1080px; width:1080px\" /></a></p>\r\n\r\n<p><strong>1, Chụp ảnh hộ chiếu,&nbsp;<a href=\"https://hi.com.vn/tag/ve-may-bay\">v&eacute; m&aacute;y bay</a>, check-in ở s&acirc;n bay v&agrave; check-in lu&ocirc;n&hellip; bầu kh&iacute; quyển</strong></p>\r\n\r\n<p>Với những chuyến&nbsp;<a href=\"https://hi.com.vn/du-lich-trong-nuoc\">du lịch trong nước</a>&nbsp;hoặc nước ngo&agrave;i khi cần di chuyển bằng m&aacute;y bay, rất nhiều người th&iacute;ch th&uacute; chụp lại h&agrave;nh tr&igrave;nh chuyến bay n&agrave;y. Những bức ảnh chụp lại v&eacute; m&aacute;y bay v&agrave; hộ chiếu, đến những bức ảnh đang c&oacute; mặt ở s&acirc;n bay hay những bức ảnh chụp những đ&aacute;m m&acirc;y ngo&agrave;i cửa sổ, mặt đất nh&igrave;n từ tr&ecirc;n cao&hellip; đều c&oacute; thể dễ d&agrave;ng bắt gặp trong những album ảnh du lịch của nhiều người.</p>\r\n\r\n<p>Cũng c&oacute; những lưu &yacute; khi chụp những kiểu ảnh rất &ldquo;hot&rdquo; n&agrave;y, c&aacute;c bạn kh&ocirc;ng n&ecirc;n chụp hết th&ocirc;ng tin tr&ecirc;n v&eacute; m&aacute;y bay l&ecirc;n ảnh v&agrave; đăng l&ecirc;n mạng khi chưa l&agrave;m mờ những th&ocirc;ng tin c&aacute; nh&acirc;n, c&oacute; những&nbsp;<a href=\"https://hi.com.vn/cam-nang/dang-anh-ve-may-bay-len-mang-nguy-hiem-rinh-rap.html\">nguy hiểm r&igrave;nh rập khi đăng&nbsp;ảnh v&eacute; m&aacute;y bay</a>&nbsp;như thế&nbsp;m&agrave; bạn kh&ocirc;ng lường trước được như việc bị mất th&ocirc;ng tin c&aacute; nh&acirc;n, mất chỗ tr&ecirc;n m&aacute;y bay&hellip;</p>\r\n\r\n<p><a href=\"https://hi.com.vn/wp-content/uploads/2016/07/nhung-kieu-anh-duoc-ua-thich-khi-di-du-lich-4.jpg\" rel=\"prettyPhoto\"><img alt=\"nhung-kieu-anh-duoc-ua-thich-khi-di-du-lich-4\" src=\"https://hi.com.vn/wp-content/uploads/2016/07/nhung-kieu-anh-duoc-ua-thich-khi-di-du-lich-4.jpg\" style=\"height:1365px; width:2048px\" /></a></p>\r\n\r\n<p>Khi chụp ảnh cảnh b&ecirc;n ngo&agrave;i cửa sổ m&aacute;y bay, c&oacute; một tip nhỏ cho bạn l&agrave; thời điểm l&iacute; tưởng nhất cho bức ảnh cửa sổ m&aacute;y bay đẹp l&agrave; l&uacute;c m&aacute;y bay chuẩn bị hạ c&aacute;nh, v&agrave; tất nhi&ecirc;n, bức ảnh sẽ đẹp nhất khi điều kiện thời tiết thuận lợi, điều n&agrave;y th&igrave; hơi h&ecirc;n xui. C&aacute;c bạn cũng cần lưu &yacute;, một số h&atilde;ng h&agrave;ng kh&ocirc;ng c&oacute; những quy định về việc chụp ảnh v&agrave; quay phim tr&ecirc;n m&aacute;y bay n&ecirc;n h&atilde;y ch&uacute; &yacute; xem việc bạn chụp ảnh khi ngồi tr&ecirc;n m&aacute;y bay c&oacute; vi phạm điều g&igrave; kh&ocirc;ng nh&eacute; (bạn h&atilde;y tham khảo th&ecirc;m b&agrave;i viết&nbsp;<a href=\"https://hi.com.vn/cam-nang/nhung-dieu-khong-nen-lam-khi-di-may-bay.html\">những điều kh&ocirc;ng n&ecirc;n l&agrave;m khi đi m&aacute;y bay</a>&nbsp;n&agrave;y nh&eacute;).</p>\r\n\r\n<p><a href=\"https://hi.com.vn/wp-content/uploads/2016/07/nhung-kieu-anh-duoc-ua-thich-khi-di-du-lich-2.jpg\" rel=\"prettyPhoto\"><img alt=\"nhung-kieu-anh-duoc-ua-thich-khi-di-du-lich-2\" src=\"https://hi.com.vn/wp-content/uploads/2016/07/nhung-kieu-anh-duoc-ua-thich-khi-di-du-lich-2.jpg\" style=\"height:600px; width:800px\" /></a></p>\r\n\r\n<p>&nbsp;<strong>2. Chụp ảnh kh&aacute;ch sạn, nh&agrave; nghỉ, nh&agrave; lưu tr&uacute;</strong></p>\r\n\r\n<p>Ghi lại những h&igrave;nh ảnh nơi m&igrave;nh nghỉ ch&acirc;n trong chuyến đi cũng rất hay ho. Với những thiết kế nội thất đẹp v&agrave; độc đ&aacute;o của kh&aacute;ch sạn, nh&agrave; nghỉ, qu&aacute;n trọ ở những điểm du lịch, bạn c&oacute; thể c&oacute; những bức h&igrave;nh tĩnh vật tuyệt vời đ&oacute;.</p>\r\n\r\n<p><a href=\"https://hi.com.vn/wp-content/uploads/2016/07/nhung-kieu-anh-duoc-ua-thich-khi-di-du-lich-6.jpg\" rel=\"prettyPhoto\"><img alt=\"nhung-kieu-anh-duoc-ua-thich-khi-di-du-lich-6\" src=\"https://hi.com.vn/wp-content/uploads/2016/07/nhung-kieu-anh-duoc-ua-thich-khi-di-du-lich-6.jpg\" style=\"height:637px; width:960px\" /></a></p>\r\n\r\n<p><strong>3, Chụp ảnh đồ ăn</strong></p>\r\n\r\n<p>Chẳng c&oacute; g&igrave; c&oacute; thể l&agrave;m l&ograve;ng dậy s&oacute;ng bằng ảnh đồ ăn cả v&agrave; c&oacute; lẽ đồ ăn cũng l&agrave; &ldquo;mẫu ảnh&rdquo; c&oacute; duy&ecirc;n nhất, chụp kiểu g&igrave; cũng đẹp. Những m&oacute;n ăn nổi tiếng, những m&oacute;n đặc sản, hay chỉ l&agrave; một m&oacute;n vặt bạn ăn khi đi du lịch sẽ đều c&oacute; thể cho ra đời một bức ảnh hay ho, ghi nhớ lại một kỉ niệm trong chuyến đi, nhắc về n&eacute;t văn h&oacute;a ẩm thực của nơi bạn đến.</p>\r\n\r\n<p><a href=\"https://hi.com.vn/wp-content/uploads/2016/07/nhung-kieu-anh-duoc-ua-thich-khi-di-du-lich-8.jpg\" rel=\"prettyPhoto\"><img alt=\"nhung-kieu-anh-duoc-ua-thich-khi-di-du-lich-8\" src=\"https://hi.com.vn/wp-content/uploads/2016/07/nhung-kieu-anh-duoc-ua-thich-khi-di-du-lich-8.jpg\" style=\"height:1063px; width:1600px\" /></a></p>\r\n\r\n<p><strong>4, Chụp ảnh biểu tượng của điểm đến</strong></p>\r\n\r\n<p>Giống như việc đến Hội An phải chụp h&igrave;nh đ&egrave;n lồng v&agrave; hoa giấy,<a href=\"https://hi.com.vn/du-lich-trong-nuoc/nhung-kinh-nghiem-di-du-lich-mien-nui-phia-bac.html\">&nbsp;du lịch&nbsp;miền n&uacute;i ph&iacute;a Bắc</a>&nbsp;phải chụp ruộng bậc thang, đến&nbsp;<a href=\"https://hi.com.vn/tag/mien-tay\">miền T&acirc;y</a>&nbsp;th&igrave; phải chụp những d&ograve;ng k&ecirc;nh, d&ugrave; chuyện g&igrave; xảy ra bạn cũng lu&ocirc;n nhớ chụp những biểu tượng nổi tiếng của nơi bạn đến nh&eacute;. Đ&oacute; &nbsp;l&agrave; một c&aacute;ch &ldquo;cộp dấu&rdquo;, t&iacute;ch list cho bản đồ du lịch của bạn đ&oacute;. M&agrave; cũng ch&iacute;nh l&agrave; những bức h&igrave;nh &ldquo;hơn vạn lời n&oacute;i&rdquo;, bạn chẳng cần n&oacute;i g&igrave; nhiều, người ta nh&igrave;n c&aacute;i cũng biết ngay bạn muốn khoe g&igrave; lu&ocirc;n.</p>\r\n\r\n<p><a href=\"https://hi.com.vn/wp-content/uploads/2016/07/nhung-kieu-anh-duoc-ua-thich-khi-di-du-lich-9.jpg\" rel=\"prettyPhoto\"><img alt=\"nhung-kieu-anh-duoc-ua-thich-khi-di-du-lich-9\" src=\"https://hi.com.vn/wp-content/uploads/2016/07/nhung-kieu-anh-duoc-ua-thich-khi-di-du-lich-9.jpg\" style=\"height:682px; width:1024px\" /></a></p>\r\n\r\n<p>RELATED POSTS</p>\r\n\r\n<p><a href=\"https://hi.com.vn/du-lich-trong-nuoc/top-10-dia-diem-du-lich-noi-tieng-nhat-nam-dinh.html\">TOP 10 địa điểm du lịch nổi tiếng nhất Nam Định</a></p>\r\n\r\n<p><a href=\"https://hi.com.vn/du-lich-trong-nuoc/canh-doan-tau-di-qua-nui-dep-nhu-mo.html\">Cảnh đo&agrave;n t&agrave;u đi qua n&uacute;i đẹp như mơ</a></p>\r\n\r\n<p><a href=\"https://hi.com.vn/du-lich-trong-nuoc/kham-pha-vinh-horseshoe-ben-ria-tam-giac-quy-bermuda.html\">Kh&aacute;m ph&aacute; vịnh Horseshoe b&ecirc;n r&igrave;a Tam gi&aacute;c quỷ Bermuda</a></p>\r\n\r\n<p><strong>&nbsp;5, Chụp ảnh thi&ecirc;n nhi&ecirc;n l&uacute;c ho&agrave;ng h&ocirc;n hoặc b&igrave;nh minh</strong></p>\r\n\r\n<p>Đứng trước những thời khắc đặc biệt như ho&agrave;ng h&ocirc;n hay b&igrave;nh minh, l&ograve;ng người lu&ocirc;n xao động những cảm x&uacute;c kh&oacute; diễn tả th&agrave;nh lời. C&oacute; những bức h&igrave;nh ghi lại những khoảnh khắc đ&oacute; sẽ rất tuyệt phải kh&ocirc;ng. Kh&ocirc;ng chỉ l&agrave; thi&ecirc;n nhi&ecirc;n, bạn tất nhi&ecirc;n cũng c&oacute; thể g&oacute;p mặt trong một bức ảnh chụp ho&agrave;ng h&ocirc;n, b&igrave;nh minh, đ&oacute; chắc chắn sẽ l&agrave; l&uacute;c&nbsp;phẩm chất&nbsp;nghệ sĩ của bạn b&ugrave;ng ch&aacute;y v&agrave; rất dễ kiếm được những bức &ldquo;để đời&rdquo;.</p>\r\n\r\n<p><a href=\"https://hi.com.vn/wp-content/uploads/2016/07/nhung-kieu-anh-duoc-ua-thich-khi-di-du-lich-10.jpg\" rel=\"prettyPhoto\"><img alt=\"nhung-kieu-anh-duoc-ua-thich-khi-di-du-lich-10\" src=\"https://hi.com.vn/wp-content/uploads/2016/07/nhung-kieu-anh-duoc-ua-thich-khi-di-du-lich-10.jpg\" style=\"height:640px; width:1024px\" /></a></p>\r\n\r\n<p><strong>6,&nbsp;Chụp ảnh&nbsp;selfie</strong></p>\r\n\r\n<p>Th&ocirc;i, ch&uacute;ng m&igrave;nh chẳng phải n&oacute;i g&igrave; nhiều nữa, tất nhi&ecirc;n l&agrave; kh&ocirc;ng thể thiếu selfie rồi.</p>\r\n\r\n<p><a href=\"https://hi.com.vn/wp-content/uploads/2016/07/nhung-kieu-anh-duoc-ua-thich-khi-di-du-lich-12.jpg\" rel=\"prettyPhoto\"><img alt=\"nhung-kieu-anh-duoc-ua-thich-khi-di-du-lich-12\" src=\"https://hi.com.vn/wp-content/uploads/2016/07/nhung-kieu-anh-duoc-ua-thich-khi-di-du-lich-12.jpg\" style=\"height:674px; width:1024px\" /></a></p>\r\n\r\n<p><strong>7, Chụp bức h&igrave;nh theo nhau đi khắp thế gian</strong></p>\r\n\r\n<p><a href=\"https://hi.com.vn/wp-content/uploads/2016/07/nhung-kieu-anh-duoc-ua-thich-khi-di-du-lich-13.jpg\" rel=\"prettyPhoto\"><img alt=\"nhung-kieu-anh-duoc-ua-thich-khi-di-du-lich-13\" src=\"https://hi.com.vn/wp-content/uploads/2016/07/nhung-kieu-anh-duoc-ua-thich-khi-di-du-lich-13.jpg\" style=\"height:720px; width:960px\" /></a></p>\r\n\r\n<p>Từ khi Murad Osmann l&agrave;m cho tr&agrave;o lưu Follow me trở th&agrave;nh hiện tượng to&agrave;n cầu, kiểu chụp ảnh n&agrave;y đ&atilde; trở th&agrave;nh kiểu ảnh nhất định phải c&oacute; trong chuyến du lịch của c&aacute;c cặp đ&ocirc;i, v&agrave; đ&ocirc;i khi l&agrave; những kiểu ảnh chế h&agrave;i hước của những người th&iacute;ch nghịch ngợm nữa. Bạn đ&atilde; c&oacute; kiểu ảnh n&agrave;y chưa?</p>\r\n\r\n<p><a href=\"https://hi.com.vn/wp-content/uploads/2016/07/nhung-kieu-anh-duoc-ua-thich-khi-di-du-lich-14.jpg\" rel=\"prettyPhoto\"><img alt=\"nhung-kieu-anh-duoc-ua-thich-khi-di-du-lich-14\" src=\"https://hi.com.vn/wp-content/uploads/2016/07/nhung-kieu-anh-duoc-ua-thich-khi-di-du-lich-14.jpg\" style=\"height:960px; width:542px\" /></a></p>\r\n\r\n<p><strong>8. N&agrave;o m&igrave;nh c&ugrave;ng nhảy</strong></p>\r\n\r\n<p>Kiểu ảnh m&agrave; tất cả mọi người c&ugrave;ng nhảy l&ecirc;n &ldquo;thật l&agrave; yomost&rdquo; c&oacute; thể xem l&agrave;&nbsp;<strong>kiểu ảnh kinh điển của những chuy&ecirc;n du lịch</strong>. Chụp những bức ảnh n&agrave;y đ&ocirc;i khi vất vả v&agrave; kh&oacute; th&agrave;nh c&ocirc;ng khi m&agrave; cả người chụp v&agrave; người được chụp đều phải cố gắng bắt được đ&uacute;ng những l&uacute;c đồng điệu nhất, đẹp nhất. Tuy nhi&ecirc;n, đ&acirc;y cũng ch&iacute;nh l&agrave; những bức để lại dư &acirc;m hứng khởi nhất m&atilde;i về sau.</p>\r\n\r\n<p><a href=\"https://hi.com.vn/wp-content/uploads/2016/07/nhung-kieu-anh-duoc-ua-thich-khi-di-du-lich-15.jpg\" rel=\"prettyPhoto\"><img alt=\"nhung-kieu-anh-duoc-ua-thich-khi-di-du-lich-15\" src=\"https://hi.com.vn/wp-content/uploads/2016/07/nhung-kieu-anh-duoc-ua-thich-khi-di-du-lich-15.jpg\" style=\"height:793px; width:800px\" /></a></p>\r\n\r\n<p><strong>9, Chụp&hellip; ch&acirc;n</strong></p>\r\n\r\n<p>Chụp ch&acirc;n l&agrave; một tr&agrave;o lưu nổi rần rần tr&ecirc;n instagram một thời. Th&ocirc;i th&igrave;, r&otilde; r&agrave;ng ch&uacute;ng m&igrave;nh<strong>&nbsp;đi du lịch</strong>&nbsp;m&agrave;, chụp ch&acirc;n l&agrave; kh&ocirc;ng c&ograve;n g&igrave; phải b&agrave;n c&atilde;i nữa rồi.</p>\r\n\r\n<p><a href=\"https://hi.com.vn/wp-content/uploads/2016/07/nhung-kieu-anh-duoc-ua-thich-khi-di-du-lich-11.jpg\" rel=\"prettyPhoto\"><img alt=\"nhung-kieu-anh-duoc-ua-thich-khi-di-du-lich-11\" src=\"https://hi.com.vn/wp-content/uploads/2016/07/nhung-kieu-anh-duoc-ua-thich-khi-di-du-lich-11.jpg\" style=\"height:658px; width:870px\" /></a></p>\r\n', 'http://localhost:81/tour/admin/uploads/Images/nhung-kieu-anh-duoc-ua-thich-khi-di-du-lich-13.jpg', '2018-06-19 00:00:00', b'0'),
