-- Cấu trúc bảng cho bảng `tour`
--

CREATE TABLE `tour` (
  `Id` int(11) NOT NULL,
  `MaTour` varchar(50) COLLATE utf8_unicode_ci NOT NULL,
  `TenTour` varchar(255) COLLATE utf8_unicode_ci NOT NULL,
  `NgayBatDau` date NOT NULL,
  `NgayKetThuc` date NOT NULL,
  `MaDM` varchar(10) COLLATE utf8_unicode_ci NOT NULL,
  `GiaTien` float NOT NULL,
  `TinhTrang` tinyint(4) NOT NULL,
  `SoLuongKhach` int(11) NOT NULL,
  `LoaiTour` int(11) DEFAULT NULL,
  `DiaDiem` int(11) DEFAULT NULL,
  `DieuKhoan` text COLLATE utf8_unicode_ci NOT NULL,
  `MoTa` text COLLATE utf8_unicode_ci NOT NULL,
  `GiaTourBaoGom` text COLLATE utf8_unicode_ci,
  `GiaTourKhongBaoGom` text COLLATE utf8_unicode_ci,
  `LichTrinh` text COLLATE utf8_unicode_ci,
  `PhuThu` text COLLATE utf8_unicode_ci,
  `HinhAnh` text COLLATE utf8_unicode_ci NOT NULL,
  `NgayTao` datetime DEFAULT CURRENT_TIMESTAMP,
  `Xoa` bit(1) NOT NULL DEFAULT b'0'
) ENGINE=InnoDB DEFAULT CHARSET=utf8 COLLATE=utf8_unicode_ci;

--
-- Đang đổ dữ liệu cho bảng `tour`
--

INSERT INTO `tour` (`Id`, `MaTour`, `TenTour`, `NgayBatDau`, `NgayKetThuc`, `MaDM`, `GiaTien`, `TinhTrang`, `SoLuongKhach`, `LoaiTour`, `DiaDiem`, `DieuKhoan`, `MoTa`, `GiaTourBaoGom`, `GiaTourKhongBaoGom`, `LichTrinh`, `PhuThu`, `HinhAnh`, `NgayTao`, `Xoa`) VALUES
(1, 'CAM', 'Campuchia', '2018-04-06', '2018-04-05', '', 500000, 0, 100, 7, 1, '<p>sdsdg</p>\r\n', '<h3>NG&Agrave;Y 01: CHIANG MAI &ndash; KONTOK &nbsp;(BỮA TỐI)</h3>\r\n\r\n<p>Hướng dẫn vi&ecirc;n đ&oacute;n kh&aacute;ch tại&nbsp;<strong>S&acirc;n Bay Chiang Mai</strong>&nbsp;hoặc&nbsp;<strong>Trạm xe lửa</strong>.</p>\r\n\r\n<p>Sau khi nhận ph&ograve;ng, HDV sẽ dẫn kh&aacute;ch thưởng thức ẩm thực&nbsp;<strong>miền Bắc Th&aacute;i Lan</strong>. Du kh&aacute;ch sẽ được thưởng thức bữa tối theo phong c&aacute;ch&nbsp;<strong>Khantoke</strong>. Phục vụ c&aacute;c m&oacute;n ăn lớn tr&ecirc;n c&aacute;c b&agrave;n thấp, thưởng thức c&aacute;c m&oacute;n ăn mang phong c&aacute;ch miền Bắc, bao gồm cả m&oacute;n ớt chi&ecirc;n với rau hấp, thịt lợn gi&ograve;n, c&aacute;nh g&agrave; chi&ecirc;n, x&uacute;c x&iacute;ch cay v&agrave; m&oacute;n thịt heo cổ điển của Miến Điện.</p>\r\n\r\n<p><img alt=\"\" src=\"https://cdn2.ivivu.com/2018/04/13/17/ivivu-khantoke-dinner.jpg\" style=\"height:399px; width:710px\" /></p>\r\n\r\n<p>Trong suốt bữa ăn, qu&yacute; kh&aacute;ch sẽ được thưởng thức c&aacute;c&nbsp;<strong>điệu nhảy truyền thống</strong>&nbsp;của c&aacute;c d&acirc;n tộc miền n&uacute;i Th&aacute;i Lan.</p>\r\n\r\n<p><img alt=\"\" src=\"https://cdn2.ivivu.com/2018/04/13/17/ivivu-bua-toi-theo-phong-cach-khantoke.jpg\" style=\"height:399px; width:710px\" /></p>\r\n\r\n<p>Ghi ch&uacute;:</p>\r\n\r\n<p>*Giờ đ&oacute;n kh&aacute;ch: 18:30 pm.</p>\r\n\r\n<p>*Thời gian: 19:30 &ndash; 21:00.</p>\r\n\r\n<h3>NG&Agrave;Y 02: CITY TOUR CHIANGMAI (ĂN S&Aacute;NG, TRƯA V&Agrave; TỐI)</h3>\r\n\r\n<p>Hướng Dẫn Vi&ecirc;n đ&oacute;n kh&aacute;ch v&agrave;o l&uacute;c 8:30 s&aacute;ng v&agrave; bắt đầu tham quan c&aacute;c ng&ocirc;i ch&ugrave;a cổ v&agrave; kh&aacute;m ph&aacute; nền văn h&oacute;a phật gi&aacute;o tại&nbsp;<strong>Chiang Mai</strong>&nbsp;bằng loại xe k&eacute;o truyền thống.</p>\r\n\r\n<p>Bắt đầu từ&nbsp;<strong>ch&ugrave;a Wat Chedi Luang</strong>&nbsp;v&agrave; tiếp tục l&agrave;&nbsp;<strong>Wat Phra Singh</strong>,&nbsp;<strong>Wat Bubpharam</strong>&nbsp;những nơi c&oacute; kiến tr&uacute;c v&agrave; thiết kế độc đ&aacute;o với những sảnh ch&ugrave;a đầy m&agrave;u sắc sặc sỡ.</p>\r\n\r\n<p><img alt=\"\" src=\"https://cdn2.ivivu.com/2018/04/13/17/ivivu-wat-pra-singh.jpg\" style=\"height:399px; width:710px\" /></p>\r\n\r\n<p>Về kh&aacute;ch sạn nghỉ ngơi trước khi HDV đ&oacute;n kh&aacute;ch đưa đi ăn tối tại&nbsp;<strong>nh&agrave; h&agrave;ng Gallery</strong>&nbsp;gần b&ecirc;n song với lối kiến tr&uacute;c đ&iacute;nh đ&aacute; qu&yacute; độc đ&aacute;o của&nbsp;<strong>Chiang Mai</strong>.</p>\r\n\r\n<h3>NG&Agrave;Y 03: TH&Aacute;NH ĐỊA VOI RỪNG &ndash; VƯỜN TH&Uacute; Đ&Ecirc;M CHIANG MAI ( ĂN S&Aacute;NG, TRƯA V&Agrave; TỐI)</h3>\r\n\r\n<p>6:30 đ&oacute;n kh&aacute;ch v&agrave; trải nghiệm nửa ng&agrave;y tại&nbsp;<strong>Elephant Jungle Sanctuary</strong>&nbsp;sẽ gi&uacute;p cho du kh&aacute;ch c&oacute; 1 khoảng thời gian đ&aacute;ng nhớ với đ&agrave;n voi trong ch&iacute;nh ng&ocirc;i nh&agrave; tự nhi&ecirc;n. Kh&aacute;ch được trực tiếp cho voi ăn, chơi đ&ugrave;a với ch&uacute;ng trong m&ocirc;i trường tự nhi&ecirc;n. Chụp h&igrave;nh, tấm b&ugrave;n với Voi, đi bộ tới con s&ocirc;n v&agrave; tắm cho ch&uacute;ng.</p>\r\n\r\n<p><img alt=\"\" src=\"https://cdn2.ivivu.com/2018/04/13/17/ivivu-elephant-jungle-sanctuary.jpg\" style=\"height:399px; width:710px\" /></p>\r\n\r\n<p>12:00 rời khỏi&nbsp;<strong>Elephant Jungle Sanctuary</strong>&nbsp;v&agrave; trở về&nbsp;<strong>ChiangMai</strong>.</p>\r\n\r\n<p>Ăn trưa tr&ecirc;n đường v&agrave; qu&yacute; kh&aacute;ch tặng 1 suất&nbsp;<strong>massage Th&aacute;i Lan</strong>&nbsp;truyền thống trước khi trở về kh&aacute;ch sạn.</p>\r\n\r\n<p>Qu&yacute; kh&aacute;ch ăn tối tại nh&agrave; h&agrave;ng địa phương v&agrave; sau đ&oacute; chuẩn bị đi&nbsp;<strong>Chiang Mai Night Safari.</strong></p>\r\n\r\n<h3>NG&Agrave;Y 04: CHIANGMAI &ndash; VIETNAM (ĂN S&Aacute;NG)</h3>\r\n\r\n<p>Hướng dẫn vi&ecirc;n đưa kh&aacute;ch ra&nbsp;<strong>s&acirc;n bay Chiang Mai</strong>&nbsp;trở về Việt Nam.</p>\r\n', NULL, NULL, NULL, NULL, 'http://localhost:81/tour/admin/uploads/Images/ivivu-phnom-penh.jpg', '2018-06-05 16:43:17', b'0'),
